import React from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Box,
  Container,
  Typography,
  Button,
  Grid,
  Card,
  CardMedia,
  CardContent,
  CardActions,
} from '@mui/material';

const featuredProducts = [
  {
    id: 1,
    name: 'Professional Telescope',
    description: 'High-powered telescope for deep space observation',
    image: '/images/telescope.jpg',
    price: '$599.99',
  },
  {
    id: 2,
    name: 'Solar System Model',
    description: 'Interactive 3D model of our solar system',
    image: '/images/solar-system.jpg',
    price: '$89.99',
  },
  {
    id: 3,
    name: 'Space Explorer Kit',
    description: 'Educational kit for young astronomers',
    image: '/images/explorer-kit.jpg',
    price: '$49.99',
  },
];

const Home = () => {
  const navigate = useNavigate();

  return (
    <>
      <Box
        sx={{
          height: '100vh',
          display: 'flex',
          alignItems: 'center',
          background: `url('/images/hero-bg.jpg') no-repeat center center`,
          backgroundSize: 'cover',
          position: 'relative',
          '&::before': {
            content: '""',
            position: 'absolute',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            background: 'rgba(0,0,0,0.5)',
          },
        }}
      >
        <Container sx={{ position: 'relative', zIndex: 1 }}>
          <Typography
            variant="h1"
            sx={{
              fontSize: { xs: '2.5rem', md: '4rem' },
              fontWeight: 700,
              mb: 4,
              textShadow: '2px 2px 4px rgba(0,0,0,0.5)',
            }}
          >
            Explore the Cosmos
          </Typography>
          <Typography
            variant="h5"
            sx={{
              mb: 4,
              maxWidth: '600px',
              textShadow: '1px 1px 2px rgba(0,0,0,0.5)',
            }}
          >
            Discover our collection of astronomical equipment, educational toys, and space-themed products
          </Typography>
          <Button
            variant="contained"
            size="large"
            onClick={() => navigate('/products')}
            sx={{
              background: 'rgba(124, 77, 255, 0.8)',
              '&:hover': { background: 'rgba(124, 77, 255, 1)' },
            }}
          >
            Shop Now
          </Button>
        </Container>
      </Box>

      <Container sx={{ py: 8 }}>
        <Typography variant="h2" sx={{ mb: 6, textAlign: 'center' }}>
          Featured Products
        </Typography>
        <Grid container spacing={4}>
          {featuredProducts.map((product) => (
            <Grid item key={product.id} xs={12} sm={6} md={4}>
              <Card
                sx={{
                  height: '100%',
                  display: 'flex',
                  flexDirection: 'column',
                  background: 'rgba(255,255,255,0.05)',
                  backdropFilter: 'blur(10px)',
                  border: '1px solid rgba(255,255,255,0.1)',
                }}
              >
                <CardMedia
                  component="img"
                  height="200"
                  image={product.image}
                  alt={product.name}
                  sx={{ objectFit: 'cover' }}
                />
                <CardContent sx={{ flexGrow: 1 }}>
                  <Typography gutterBottom variant="h5" component="h2">
                    {product.name}
                  </Typography>
                  <Typography>{product.description}</Typography>
                  <Typography variant="h6" sx={{ mt: 2, color: 'secondary.main' }}>
                    {product.price}
                  </Typography>
                </CardContent>
                <CardActions>
                  <Button size="small" color="secondary">
                    Add to Cart
                  </Button>
                  <Button size="small" color="secondary">
                    Learn More
                  </Button>
                </CardActions>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Container>
    </>
  );
};

export default Home;
