import React from 'react';
import { Link } from 'react-router-dom';
import {
  Box,
  Container,
  Grid,
  Typography,
  IconButton,
  List,
  ListItem,
  ListItemText,
} from '@mui/material';
import {
  Facebook,
  Twitter,
  Instagram,
  LinkedIn,
} from '@mui/icons-material';

const categories = [
  { name: 'Telescopes & Accessories', path: '/products' },
  { name: 'Space Merchandise', path: '/products' },
  { name: 'Books & Education', path: '/products' },
  { name: 'Home & Lifestyle', path: '/products' },
  { name: 'Gadgets & Kits', path: '/products' },
];

const Footer = () => {
  return (
    <Box
      component="footer"
      sx={{
        background: 'rgba(0,0,0,0.5)',
        backdropFilter: 'blur(10px)',
        borderTop: '1px solid rgba(255,255,255,0.1)',
        mt: 'auto',
        py: 6,
      }}
    >
      <Container maxWidth="lg">
        <Grid container spacing={4}>
          <Grid item xs={12} sm={4}>
            <Typography variant="h6" color="white" gutterBottom>
              About Us
            </Typography>
            <Typography variant="body2" color="rgba(255,255,255,0.7)">
              Discover the cosmos through our carefully curated collection of telescopes,
              space merchandise, and educational materials. Your journey to the stars begins here.
            </Typography>
          </Grid>
          <Grid item xs={12} sm={4}>
            <Typography variant="h6" color="white" gutterBottom>
              Categories
            </Typography>
            <List>
              {categories.map((category) => (
                <ListItem 
                  key={category.name} 
                  component={Link} 
                  to={category.path}
                  sx={{
                    p: 0,
                    mb: 1,
                    color: 'rgba(255,255,255,0.7)',
                    textDecoration: 'none',
                    '&:hover': {
                      color: '#7c4dff',
                    },
                  }}
                >
                  <ListItemText primary={category.name} />
                </ListItem>
              ))}
            </List>
          </Grid>
          <Grid item xs={12} sm={4}>
            <Typography variant="h6" color="white" gutterBottom>
              Connect With Us
            </Typography>
            <Box sx={{ mt: 2 }}>
              <IconButton
                sx={{
                  mr: 1,
                  color: 'rgba(255,255,255,0.7)',
                  '&:hover': {
                    color: '#7c4dff',
                    transform: 'translateY(-2px)',
                  },
                  transition: 'all 0.3s ease-in-out',
                }}
              >
                <Facebook />
              </IconButton>
              <IconButton
                sx={{
                  mr: 1,
                  color: 'rgba(255,255,255,0.7)',
                  '&:hover': {
                    color: '#7c4dff',
                    transform: 'translateY(-2px)',
                  },
                  transition: 'all 0.3s ease-in-out',
                }}
              >
                <Twitter />
              </IconButton>
              <IconButton
                sx={{
                  mr: 1,
                  color: 'rgba(255,255,255,0.7)',
                  '&:hover': {
                    color: '#7c4dff',
                    transform: 'translateY(-2px)',
                  },
                  transition: 'all 0.3s ease-in-out',
                }}
              >
                <Instagram />
              </IconButton>
              <IconButton
                sx={{
                  color: 'rgba(255,255,255,0.7)',
                  '&:hover': {
                    color: '#7c4dff',
                    transform: 'translateY(-2px)',
                  },
                  transition: 'all 0.3s ease-in-out',
                }}
              >
                <LinkedIn />
              </IconButton>
            </Box>
            <Typography 
              variant="body2" 
              color="rgba(255,255,255,0.7)"
              sx={{ mt: 2 }}
            >
              Subscribe to our newsletter for updates on new products and cosmic events.
            </Typography>
          </Grid>
        </Grid>
        <Typography
          variant="body2"
          color="rgba(255,255,255,0.7)"
          align="center"
          sx={{ mt: 4 }}
        >
          {new Date().getFullYear()} Cosmos Shop. All rights reserved.
        </Typography>
      </Container>
    </Box>
  );
};

export default Footer;
