import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import {
  Box,
  Container,
  Grid,
  Card,
  CardContent,
  CardMedia,
  Typography,
  TextField,
  Slider,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  InputAdornment,
} from '@mui/material';
import { Search as SearchIcon } from '@mui/icons-material';

const products = [
  {
    id: 1,
    name: 'Matrix Phoenix Refractor Telescope',
    description: '60/700 refractor, aluminum tripod, 1.25" eyepieces',
    price: 3999,
    category: 'Telescopes & Accessories',
    image: 'https://images.unsplash.com/photo-1579532537598-459ecdaf39cc?auto=format&fit=crop&w=1024&q=80',
    target: 'Beginners',
  },
  {
    id: 2,
    name: 'Celestron AstroMaster Motor Drive',
    description: 'Motor drive, automatic tracking',
    price: 32999,
    category: 'Telescopes & Accessories',
    image: 'https://images.unsplash.com/photo-1543722530-d2c3201371e7?auto=format&fit=crop&w=1024&q=80',
    target: 'Professional astronomers',
  },
  {
    id: 3,
    name: 'SSEA Professional Astronomical',
    description: '70mm aperture, phone adapter',
    price: 7499,
    category: 'Telescopes & Accessories',
    image: 'https://images.unsplash.com/photo-1566937169390-7be4c63b8a0e?auto=format&fit=crop&w=1024&q=80',
    target: 'Intermediate users',
  },
  {
    id: 4,
    name: 'Astronomical Reflector Telescope',
    description: '114mm aperture, motor drive',
    price: 15999,
    category: 'Telescopes & Accessories',
    image: 'https://images.unsplash.com/photo-1567322829805-d7949fa4e7bc?auto=format&fit=crop&w=1024&q=80',
    target: 'Advanced users',
  },
  {
    id: 5,
    name: 'NASA Logo T-Shirt',
    description: 'Official NASA merchandise, 100% cotton',
    price: 999,
    category: 'Space Merchandise',
    image: 'https://images.unsplash.com/photo-1517976487492-5750f3195933?auto=format&fit=crop&w=1024&q=80',
  },
  {
    id: 6,
    name: 'Astronomy Guide 2024',
    description: 'Comprehensive guide to celestial events',
    price: 799,
    category: 'Books & Education',
    image: 'https://images.unsplash.com/photo-1614935151651-0bea6508db6b?auto=format&fit=crop&w=1024&q=80',
  },
  {
    id: 7,
    name: 'Meteorite Fragment',
    description: 'Authentic meteorite specimen with display case',
    price: 2999,
    category: 'Collectibles',
    image: 'https://images.unsplash.com/photo-1581447109200-bf2769116351?auto=format&fit=crop&w=1024&q=80',
  },
  {
    id: 8,
    name: 'DIY Mars Rover Kit',
    description: 'Educational robotics kit with programming guide',
    price: 3499,
    category: 'Gadgets & Kits',
    image: 'https://images.unsplash.com/photo-1446776811953-b23d57bd21aa?auto=format&fit=crop&w=1024&q=80',
  },
  {
    id: 9,
    name: 'Galaxy Projector Pro',
    description: 'Advanced nebula and star projection system',
    price: 1999,
    category: 'Home & Lifestyle',
    image: 'https://images.unsplash.com/photo-1534293230397-c067fc201ab8?auto=format&fit=crop&w=1024&q=80',
  },
];

const categories = [
  'All',
  'Telescopes & Accessories',
  'Space Merchandise',
  'Books & Education',
  'Collectibles',
  'Gadgets & Kits',
  'Home & Lifestyle',
];

const Products = () => {
  const location = useLocation();
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [priceRange, setPriceRange] = useState([0, 35000]);

  useEffect(() => {
    if (location.state?.category) {
      setSelectedCategory(location.state.category);
    }
  }, [location.state]);

  const filteredProducts = products.filter((product) => {
    const matchesSearch = product.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
      product.description.toLowerCase().includes(searchQuery.toLowerCase());
    const matchesCategory = selectedCategory === 'All' || product.category === selectedCategory;
    const matchesPrice = product.price >= priceRange[0] && product.price <= priceRange[1];
    return matchesSearch && matchesCategory && matchesPrice;
  });

  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value);
  };

  const handleCategoryChange = (event) => {
    setSelectedCategory(event.target.value);
  };

  const handlePriceChange = (event, newValue) => {
    setPriceRange(newValue);
  };

  return (
    <Container maxWidth="lg" sx={{ py: 8 }}>
      {/* Filters Section */}
      <Box
        sx={{
          mb: 6,
          p: 3,
          borderRadius: 2,
          background: 'rgba(255,255,255,0.05)',
          backdropFilter: 'blur(10px)',
          border: '1px solid rgba(255,255,255,0.1)',
        }}
      >
        <Grid container spacing={3} alignItems="center">
          <Grid item xs={12} sm={4}>
            <TextField
              fullWidth
              variant="outlined"
              placeholder="Search products..."
              value={searchQuery}
              onChange={handleSearchChange}
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <SearchIcon sx={{ color: 'rgba(255,255,255,0.7)' }} />
                  </InputAdornment>
                ),
                sx: {
                  color: 'white',
                  '& .MuiOutlinedInput-notchedOutline': {
                    borderColor: 'rgba(255,255,255,0.23)',
                  },
                  '&:hover .MuiOutlinedInput-notchedOutline': {
                    borderColor: 'rgba(255,255,255,0.4)',
                  },
                  '&.Mui-focused .MuiOutlinedInput-notchedOutline': {
                    borderColor: '#7c4dff',
                  },
                },
              }}
            />
          </Grid>
          <Grid item xs={12} sm={4}>
            <FormControl fullWidth>
              <InputLabel 
                id="category-select-label"
                sx={{ 
                  color: 'rgba(255,255,255,0.7)',
                  '&.Mui-focused': {
                    color: '#7c4dff',
                  },
                }}
              >
                Category
              </InputLabel>
              <Select
                labelId="category-select-label"
                value={selectedCategory}
                onChange={handleCategoryChange}
                label="Category"
                sx={{
                  color: 'white',
                  '& .MuiOutlinedInput-notchedOutline': {
                    borderColor: 'rgba(255,255,255,0.23)',
                  },
                  '&:hover .MuiOutlinedInput-notchedOutline': {
                    borderColor: 'rgba(255,255,255,0.4)',
                  },
                  '&.Mui-focused .MuiOutlinedInput-notchedOutline': {
                    borderColor: '#7c4dff',
                  },
                }}
              >
                {categories.map((category) => (
                  <MenuItem key={category} value={category}>
                    {category}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>
          <Grid item xs={12} sm={4}>
            <Typography gutterBottom sx={{ color: 'rgba(255,255,255,0.7)' }}>
              Price Range: ₹{priceRange[0]} - ₹{priceRange[1]}
            </Typography>
            <Slider
              value={priceRange}
              onChange={handlePriceChange}
              min={0}
              max={35000}
              step={100}
              sx={{
                color: '#7c4dff',
                '& .MuiSlider-thumb': {
                  '&:hover, &.Mui-focusVisible': {
                    boxShadow: '0 0 0 8px rgba(124,77,255,0.16)',
                  },
                },
                '& .MuiSlider-rail': {
                  opacity: 0.32,
                },
              }}
            />
          </Grid>
        </Grid>
      </Box>

      {/* Products Grid */}
      <Grid container spacing={4}>
        {filteredProducts.map((product) => (
          <Grid item key={product.id} xs={12} sm={6} md={4}>
            <Card
              sx={{
                height: '100%',
                display: 'flex',
                flexDirection: 'column',
                background: 'rgba(255,255,255,0.05)',
                backdropFilter: 'blur(10px)',
                border: '1px solid rgba(255,255,255,0.1)',
                transition: 'all 0.3s ease-in-out',
                '&:hover': {
                  transform: 'translateY(-8px)',
                  boxShadow: '0 12px 20px rgba(0,0,0,0.2)',
                  border: '1px solid rgba(124,77,255,0.3)',
                  '& .MuiCardMedia-root': {
                    transform: 'scale(1.1)',
                  },
                },
              }}
            >
              <Box sx={{ position: 'relative', overflow: 'hidden', pt: '60%' }}>
                <CardMedia
                  component="img"
                  image={product.image}
                  alt={product.name}
                  sx={{
                    position: 'absolute',
                    top: 0,
                    left: 0,
                    width: '100%',
                    height: '100%',
                    transition: 'transform 0.3s ease-in-out',
                  }}
                />
              </Box>
              <CardContent sx={{ flexGrow: 1 }}>
                <Typography 
                  variant="h6" 
                  component="h2" 
                  sx={{ 
                    color: 'white',
                    mb: 1,
                  }}
                >
                  {product.name}
                </Typography>
                <Typography 
                  sx={{ 
                    mb: 2,
                    color: 'rgba(255,255,255,0.7)',
                  }}
                >
                  {product.description}
                </Typography>
                <Typography 
                  variant="h6" 
                  sx={{ 
                    color: '#7c4dff',
                    fontWeight: 600,
                  }}
                >
                  ₹{product.price.toLocaleString()}
                </Typography>
                {product.target && (
                  <Typography 
                    variant="caption" 
                    sx={{ 
                      display: 'block',
                      mt: 1,
                      color: 'rgba(255,255,255,0.5)',
                    }}
                  >
                    Recommended for: {product.target}
                  </Typography>
                )}
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
};

export default Products;
