import React, { useState } from 'react';
import {
  Box,
  Container,
  Typography,
  Stepper,
  Step,
  StepLabel,
  Paper,
  Grid,
  TextField,
  Button,
  Radio,
  RadioGroup,
  FormControlLabel,
  FormControl,
  FormLabel,
  Divider,
  Card,
  CardContent,
  IconButton,
} from '@mui/material';
import {
  AccountBalance as BankIcon,
  CreditCard as CardIcon,
  LocalShipping as ShippingIcon,
  Payment as PaymentIcon,
  Receipt as ReceiptIcon,
  MonetizationOn as CashIcon,
} from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';
import { useProduct } from '../context/ProductContext';

const steps = ['Shipping Address', 'Payment Method', 'Review Order'];

const Checkout = () => {
  const navigate = useNavigate();
  const { cart, getCartTotal } = useProduct();
  const [activeStep, setActiveStep] = useState(0);
  const [paymentMethod, setPaymentMethod] = useState('card');

  const handleNext = () => {
    if (activeStep === steps.length - 1) {
      navigate('/order-success');
    } else {
      setActiveStep((prevStep) => prevStep + 1);
    }
  };

  const handleBack = () => {
    setActiveStep((prevStep) => prevStep - 1);
  };

  const AddressForm = () => (
    <Grid container spacing={3}>
      <Grid item xs={12} sm={6}>
        <TextField
          required
          fullWidth
          label="First Name"
          variant="outlined"
        />
      </Grid>
      <Grid item xs={12} sm={6}>
        <TextField
          required
          fullWidth
          label="Last Name"
          variant="outlined"
        />
      </Grid>
      <Grid item xs={12}>
        <TextField
          required
          fullWidth
          label="Email"
          type="email"
          variant="outlined"
        />
      </Grid>
      <Grid item xs={12}>
        <TextField
          required
          fullWidth
          label="Phone Number"
          variant="outlined"
        />
      </Grid>
      <Grid item xs={12}>
        <TextField
          required
          fullWidth
          label="Address"
          multiline
          rows={3}
          variant="outlined"
        />
      </Grid>
      <Grid item xs={12} sm={6}>
        <TextField
          required
          fullWidth
          label="City"
          variant="outlined"
        />
      </Grid>
      <Grid item xs={12} sm={6}>
        <TextField
          required
          fullWidth
          label="PIN Code"
          variant="outlined"
        />
      </Grid>
    </Grid>
  );

  const PaymentForm = () => (
    <FormControl component="fieldset">
      <FormLabel component="legend" sx={{ mb: 3, color: 'text.primary' }}>
        Select Payment Method
      </FormLabel>
      <RadioGroup
        value={paymentMethod}
        onChange={(e) => setPaymentMethod(e.target.value)}
      >
        <Paper
          sx={{
            p: 2,
            mb: 2,
            transition: 'all 0.3s',
            border: paymentMethod === 'card' ? '2px solid #00B6F1' : '1px solid rgba(255,255,255,0.1)',
            '&:hover': {
              borderColor: '#00B6F1',
            },
          }}
        >
          <FormControlLabel
            value="card"
            control={<Radio />}
            label={
              <Box sx={{ display: 'flex', alignItems: 'center' }}>
                <CardIcon sx={{ mr: 1, color: '#00B6F1' }} />
                <Typography>Credit/Debit Card</Typography>
              </Box>
            }
          />
          {paymentMethod === 'card' && (
            <Box sx={{ mt: 2, pl: 4 }}>
              <Grid container spacing={2}>
                <Grid item xs={12}>
                  <TextField
                    fullWidth
                    label="Card Number"
                    variant="outlined"
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    fullWidth
                    label="Expiry Date"
                    placeholder="MM/YY"
                    variant="outlined"
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    fullWidth
                    label="CVV"
                    type="password"
                    variant="outlined"
                  />
                </Grid>
              </Grid>
            </Box>
          )}
        </Paper>

        <Paper
          sx={{
            p: 2,
            mb: 2,
            transition: 'all 0.3s',
            border: paymentMethod === 'upi' ? '2px solid #00B6F1' : '1px solid rgba(255,255,255,0.1)',
            '&:hover': {
              borderColor: '#00B6F1',
            },
          }}
        >
          <FormControlLabel
            value="upi"
            control={<Radio />}
            label={
              <Box sx={{ display: 'flex', alignItems: 'center' }}>
                <PaymentIcon sx={{ mr: 1, color: '#FF6B6B' }} />
                <Typography>UPI</Typography>
              </Box>
            }
          />
          {paymentMethod === 'upi' && (
            <Box sx={{ mt: 2, pl: 4 }}>
              <TextField
                fullWidth
                label="UPI ID"
                placeholder="username@upi"
                variant="outlined"
              />
            </Box>
          )}
        </Paper>

        <Paper
          sx={{
            p: 2,
            mb: 2,
            transition: 'all 0.3s',
            border: paymentMethod === 'netbanking' ? '2px solid #00B6F1' : '1px solid rgba(255,255,255,0.1)',
            '&:hover': {
              borderColor: '#00B6F1',
            },
          }}
        >
          <FormControlLabel
            value="netbanking"
            control={<Radio />}
            label={
              <Box sx={{ display: 'flex', alignItems: 'center' }}>
                <BankIcon sx={{ mr: 1, color: '#00F5D4' }} />
                <Typography>Net Banking</Typography>
              </Box>
            }
          />
        </Paper>

        <Paper
          sx={{
            p: 2,
            mb: 2,
            transition: 'all 0.3s',
            border: paymentMethod === 'cod' ? '2px solid #00B6F1' : '1px solid rgba(255,255,255,0.1)',
            '&:hover': {
              borderColor: '#00B6F1',
            },
          }}
        >
          <FormControlLabel
            value="cod"
            control={<Radio />}
            label={
              <Box sx={{ display: 'flex', alignItems: 'center' }}>
                <CashIcon sx={{ mr: 1, color: '#FF9E00' }} />
                <Typography>Cash on Delivery</Typography>
                <Typography variant="caption" sx={{ ml: 1, color: 'text.secondary' }}>
                  (Additional ₹49 fee applies)
                </Typography>
              </Box>
            }
          />
        </Paper>
      </RadioGroup>
    </FormControl>
  );

  const ReviewForm = () => (
    <Grid container spacing={3}>
      <Grid item xs={12}>
        <Typography variant="h6" gutterBottom>
          Order Summary
        </Typography>
        {cart.map((item) => (
          <Card
            key={item.id}
            sx={{
              mb: 2,
              position: 'relative',
              overflow: 'hidden',
              '&::before': {
                content: '""',
                position: 'absolute',
                top: 0,
                left: 0,
                right: 0,
                height: '2px',
                background: 'linear-gradient(90deg, #00B6F1, #4CDFFF)',
              },
            }}
          >
            <CardContent>
              <Grid container spacing={2} alignItems="center">
                <Grid item xs={2}>
                  <Box
                    component="img"
                    src={item.image}
                    alt={item.name}
                    sx={{
                      width: '100%',
                      borderRadius: 1,
                    }}
                  />
                </Grid>
                <Grid item xs={6}>
                  <Typography variant="subtitle1" gutterBottom>
                    {item.name}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    Quantity: {item.quantity}
                  </Typography>
                </Grid>
                <Grid item xs={4}>
                  <Typography variant="subtitle1" align="right">
                    ₹{(item.price * item.quantity).toLocaleString()}
                  </Typography>
                </Grid>
              </Grid>
            </CardContent>
          </Card>
        ))}
        <Box sx={{ mt: 3 }}>
          <Grid container spacing={2}>
            <Grid item xs={6}>
              <Typography>Subtotal</Typography>
            </Grid>
            <Grid item xs={6}>
              <Typography align="right">₹{getCartTotal().toLocaleString()}</Typography>
            </Grid>
            <Grid item xs={6}>
              <Typography>Shipping</Typography>
            </Grid>
            <Grid item xs={6}>
              <Typography align="right" color="success.main">
                Free
              </Typography>
            </Grid>
            {paymentMethod === 'cod' && (
              <>
                <Grid item xs={6}>
                  <Typography>COD Fee</Typography>
                </Grid>
                <Grid item xs={6}>
                  <Typography align="right">₹49</Typography>
                </Grid>
              </>
            )}
            <Grid item xs={12}>
              <Divider sx={{ my: 2 }} />
            </Grid>
            <Grid item xs={6}>
              <Typography variant="h6">Total</Typography>
            </Grid>
            <Grid item xs={6}>
              <Typography variant="h6" align="right">
                ₹{(getCartTotal() + (paymentMethod === 'cod' ? 49 : 0)).toLocaleString()}
              </Typography>
            </Grid>
          </Grid>
        </Box>
      </Grid>
    </Grid>
  );

  return (
    <Box
      sx={{
        py: 6,
        minHeight: '100vh',
      }}
    >
      <Container>
        <Paper
          sx={{
            p: { xs: 2, md: 4 },
            position: 'relative',
            overflow: 'hidden',
            '&::before': {
              content: '""',
              position: 'absolute',
              top: 0,
              left: 0,
              right: 0,
              height: '4px',
              background: 'linear-gradient(90deg, #00B6F1, #4CDFFF)',
            },
          }}
        >
          <Typography
            variant="h4"
            align="center"
            gutterBottom
            sx={{
              mb: 4,
              background: 'linear-gradient(45deg, #00B6F1 30%, #4CDFFF 90%)',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
            }}
          >
            Checkout
          </Typography>

          <Stepper
            activeStep={activeStep}
            sx={{
              mb: 5,
              '& .MuiStepLabel-label': {
                color: 'text.secondary',
                '&.Mui-active': {
                  color: '#00B6F1',
                },
                '&.Mui-completed': {
                  color: '#4CDFFF',
                },
              },
            }}
          >
            {steps.map((label) => (
              <Step key={label}>
                <StepLabel>{label}</StepLabel>
              </Step>
            ))}
          </Stepper>

          {activeStep === 0 && <AddressForm />}
          {activeStep === 1 && <PaymentForm />}
          {activeStep === 2 && <ReviewForm />}

          <Box sx={{ display: 'flex', justifyContent: 'flex-end', mt: 4 }}>
            {activeStep !== 0 && (
              <Button onClick={handleBack} sx={{ mr: 1 }}>
                Back
              </Button>
            )}
            <Button
              variant="contained"
              onClick={handleNext}
              sx={{
                minWidth: 200,
              }}
            >
              {activeStep === steps.length - 1 ? 'Place Order' : 'Next'}
            </Button>
          </Box>
        </Paper>
      </Container>
    </Box>
  );
};

export default Checkout;
