import React, { useState } from 'react';
import {
  Box,
  Grid,
  Card,
  CardContent,
  Typography,
  Button,
  IconButton,
  Rating,
  Chip,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Stack,
  Snackbar,
  Alert,
} from '@mui/material';
import {
  Favorite,
  FavoriteBorder,
  LocalShipping,
} from '@mui/icons-material';
import { useProduct } from '../context/ProductContext';

const products = [
  {
    id: 1,
    name: 'Matrix Phoenix Refractor Telescope',
    brand: 'Matrix Optics',
    description: 'Professional-grade refractor telescope with 90mm aperture',
    price: 3999,
    originalPrice: 4999,
    discount: 20,
    category: 'Telescopes & Accessories',
    subCategory: 'Refractor Telescopes',
    image: '/images/telescope1.jpg',
    sizes: ['60mm', '90mm', '120mm'],
    rating: 4.5,
    reviews: 128,
    inStock: true,
    freeDelivery: true,
  },
  {
    id: 2,
    name: 'Celestron AstroMaster Motor Drive',
    brand: 'Celestron',
    description: 'Automated tracking system for precise celestial observation',
    price: 32999,
    originalPrice: 35999,
    discount: 8,
    category: 'Telescopes & Accessories',
    subCategory: 'Telescope Accessories',
    image: '/images/motor-drive.jpg',
    sizes: ['Standard'],
    rating: 4.8,
    reviews: 89,
    inStock: true,
    freeDelivery: true,
  },
  {
    id: 3,
    name: 'NASA Apollo 11 T-Shirt',
    brand: 'Space Merch',
    description: 'Official NASA Apollo 11 commemorative t-shirt',
    price: 999,
    originalPrice: 1499,
    discount: 33,
    category: 'Space Merchandise',
    subCategory: 'Apparel',
    image: '/images/nasa-tshirt.jpg',
    sizes: ['S', 'M', 'L', 'XL'],
    rating: 4.6,
    reviews: 245,
    inStock: true,
    freeDelivery: true,
  },
  {
    id: 4,
    name: 'Astronomy: A Self-Teaching Guide',
    brand: 'Wiley Publishing',
    description: 'Comprehensive guide to understanding astronomy',
    price: 799,
    originalPrice: 999,
    discount: 20,
    category: 'Books & Education',
    subCategory: 'Books',
    image: '/images/astronomy-book.jpg',
    sizes: ['Paperback'],
    rating: 4.7,
    reviews: 167,
    inStock: true,
    freeDelivery: false,
  },
  {
    id: 5,
    name: 'Authentic Meteorite Fragment',
    brand: 'Space Collectibles',
    description: 'Certified authentic meteorite fragment with display case',
    price: 2999,
    originalPrice: 3999,
    discount: 25,
    category: 'Collectibles',
    subCategory: 'Meteorites',
    image: '/images/meteorite.jpg',
    sizes: ['Small', 'Medium'],
    rating: 4.9,
    reviews: 56,
    inStock: true,
    freeDelivery: true,
  },
];

const Products = () => {
  const [sortBy, setSortBy] = useState('featured');
  const [filterCategory, setFilterCategory] = useState('all');
  const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: 'success' });
  const { addToCart, wishlist, toggleWishlist, selectSize, getSelectedSize } = useProduct();

  const handleSort = (event) => {
    setSortBy(event.target.value);
  };

  const handleFilter = (event) => {
    setFilterCategory(event.target.value);
  };

  const handleAddToCart = (product) => {
    const selectedSize = getSelectedSize(product.id);
    if (!selectedSize) {
      setSnackbar({
        open: true,
        message: 'Please select a size first',
        severity: 'warning',
      });
      return;
    }
    addToCart(product, selectedSize);
    setSnackbar({
      open: true,
      message: 'Added to cart successfully!',
      severity: 'success',
    });
  };

  const handleCloseSnackbar = () => {
    setSnackbar({ ...snackbar, open: false });
  };

  const sortProducts = (products) => {
    switch (sortBy) {
      case 'price-low':
        return [...products].sort((a, b) => a.price - b.price);
      case 'price-high':
        return [...products].sort((a, b) => b.price - a.price);
      case 'discount':
        return [...products].sort((a, b) => b.discount - a.discount);
      case 'rating':
        return [...products].sort((a, b) => b.rating - a.rating);
      default:
        return products;
    }
  };

  const filterProducts = (products) => {
    if (filterCategory === 'all') return products;
    return products.filter((product) => product.category === filterCategory);
  };

  const displayProducts = sortProducts(filterProducts(products));
  const categories = ['all', ...new Set(products.map((product) => product.category))];

  return (
    <Box sx={{ p: { xs: 2, md: 4 } }}>
      <Box
        sx={{
          display: 'flex',
          flexDirection: { xs: 'column', sm: 'row' },
          gap: 2,
          mb: 4,
        }}
      >
        <FormControl
          sx={{
            minWidth: 200,
            '& .MuiOutlinedInput-root': {
              color: 'white',
              '& fieldset': {
                borderColor: 'rgba(255,255,255,0.3)',
              },
              '&:hover fieldset': {
                borderColor: 'rgba(255,255,255,0.5)',
              },
            },
            '& .MuiInputLabel-root': {
              color: 'rgba(255,255,255,0.7)',
            },
          }}
        >
          <InputLabel>Sort By</InputLabel>
          <Select value={sortBy} onChange={handleSort} label="Sort By">
            <MenuItem value="featured">Featured</MenuItem>
            <MenuItem value="price-low">Price: Low to High</MenuItem>
            <MenuItem value="price-high">Price: High to Low</MenuItem>
            <MenuItem value="discount">Discount</MenuItem>
            <MenuItem value="rating">Rating</MenuItem>
          </Select>
        </FormControl>
        <FormControl
          sx={{
            minWidth: 200,
            '& .MuiOutlinedInput-root': {
              color: 'white',
              '& fieldset': {
                borderColor: 'rgba(255,255,255,0.3)',
              },
              '&:hover fieldset': {
                borderColor: 'rgba(255,255,255,0.5)',
              },
            },
            '& .MuiInputLabel-root': {
              color: 'rgba(255,255,255,0.7)',
            },
          }}
        >
          <InputLabel>Category</InputLabel>
          <Select value={filterCategory} onChange={handleFilter} label="Category">
            {categories.map((category) => (
              <MenuItem key={category} value={category}>
                {category === 'all' ? 'All Categories' : category}
              </MenuItem>
            ))}
          </Select>
        </FormControl>
      </Box>
      <Grid container spacing={3}>
        {displayProducts.map((product) => (
          <Grid item key={product.id} xs={12} sm={6} md={4} lg={3}>
            <Card
              sx={{
                height: '100%',
                display: 'flex',
                flexDirection: 'column',
                bgcolor: 'rgba(0,0,0,0.5)',
                backdropFilter: 'blur(10px)',
                border: '1px solid rgba(255,255,255,0.1)',
                transition: 'transform 0.2s',
                '&:hover': {
                  transform: 'translateY(-4px)',
                },
              }}
            >
              <Box sx={{ position: 'relative', pt: '100%' }}>
                <Box
                  component="img"
                  src={product.image}
                  alt={product.name}
                  sx={{
                    position: 'absolute',
                    top: 0,
                    left: 0,
                    width: '100%',
                    height: '100%',
                    objectFit: 'cover',
                  }}
                />
                <IconButton
                  onClick={() => toggleWishlist(product.id)}
                  sx={{
                    position: 'absolute',
                    top: 8,
                    right: 8,
                    bgcolor: 'rgba(0,0,0,0.5)',
                    '&:hover': { bgcolor: 'rgba(0,0,0,0.7)' },
                  }}
                >
                  {wishlist.includes(product.id) ? (
                    <Favorite sx={{ color: '#ff3d47' }} />
                  ) : (
                    <FavoriteBorder sx={{ color: 'white' }} />
                  )}
                </IconButton>
              </Box>
              <CardContent sx={{ flexGrow: 1 }}>
                <Typography
                  variant="subtitle2"
                  sx={{ color: 'rgba(255,255,255,0.7)' }}
                >
                  {product.brand}
                </Typography>
                <Typography variant="h6" sx={{ color: 'white', mb: 1 }}>
                  {product.name}
                </Typography>
                <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                  <Rating
                    value={product.rating}
                    precision={0.1}
                    readOnly
                    size="small"
                  />
                  <Typography
                    variant="body2"
                    sx={{ ml: 1, color: 'rgba(255,255,255,0.7)' }}
                  >
                    ({product.reviews})
                  </Typography>
                </Box>
                <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                  <Typography variant="h6" sx={{ color: 'white' }}>
                    ₹{product.price}
                  </Typography>
                  <Typography
                    variant="body2"
                    sx={{
                      ml: 1,
                      textDecoration: 'line-through',
                      color: 'rgba(255,255,255,0.5)',
                    }}
                  >
                    ₹{product.originalPrice}
                  </Typography>
                  <Chip
                    label={`${product.discount}% OFF`}
                    size="small"
                    sx={{
                      ml: 1,
                      bgcolor: '#7c4dff',
                      color: 'white',
                    }}
                  />
                </Box>
                {product.freeDelivery && (
                  <Box
                    sx={{
                      display: 'flex',
                      alignItems: 'center',
                      mb: 2,
                      color: '#4caf50',
                    }}
                  >
                    <LocalShipping sx={{ fontSize: 16, mr: 0.5 }} />
                    <Typography variant="body2">Free Delivery</Typography>
                  </Box>
                )}
                <Stack direction="row" spacing={1} sx={{ mb: 2 }}>
                  {product.sizes.map((size) => (
                    <Chip
                      key={size}
                      label={size}
                      onClick={() => selectSize(product.id, size)}
                      sx={{
                        bgcolor:
                          getSelectedSize(product.id) === size
                            ? '#7c4dff'
                            : 'rgba(255,255,255,0.1)',
                        color: 'white',
                        '&:hover': {
                          bgcolor:
                            getSelectedSize(product.id) === size
                              ? '#6c3fff'
                              : 'rgba(255,255,255,0.2)',
                        },
                      }}
                    />
                  ))}
                </Stack>
                <Button
                  variant="contained"
                  fullWidth
                  onClick={() => handleAddToCart(product)}
                  sx={{
                    bgcolor: '#7c4dff',
                    '&:hover': {
                      bgcolor: '#6c3fff',
                    },
                  }}
                >
                  Add to Cart
                </Button>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
      <Snackbar
        open={snackbar.open}
        autoHideDuration={3000}
        onClose={handleCloseSnackbar}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      >
        <Alert
          onClose={handleCloseSnackbar}
          severity={snackbar.severity}
          sx={{ width: '100%' }}
        >
          {snackbar.message}
        </Alert>
      </Snackbar>
    </Box>
  );
};

export default Products;
