import React from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Box,
  Container,
  Grid,
  Typography,
  IconButton,
  Link,
} from '@mui/material';
import {
  Facebook,
  Twitter,
  Instagram,
  LinkedIn,
  GitHub,
} from '@mui/icons-material';

const categories = [
  { name: 'Telescopes', filter: 'Telescopes & Accessories' },
  { name: 'Merchandise', filter: 'Space Merchandise' },
  { name: 'Study Materials', filter: 'Books & Education' },
  { name: 'Home Decor', filter: 'Home & Lifestyle' },
  { name: 'Space Toys', filter: 'Gadgets & Kits' },
  { name: 'Galaxy Lamps', filter: 'Home & Lifestyle' },
];

const Footer = () => {
  const navigate = useNavigate();

  const handleCategoryClick = (filter) => {
    navigate('/products', { state: { category: filter } });
  };

  return (
    <Box
      component="footer"
      sx={{
        bgcolor: 'rgba(0,0,0,0.8)',
        backdropFilter: 'blur(10px)',
        color: 'white',
        py: 6,
        borderTop: '1px solid rgba(255,255,255,0.1)',
      }}
    >
      <Container maxWidth="lg">
        <Grid container spacing={4}>
          <Grid item xs={12} sm={4}>
            <Typography variant="h6" sx={{ mb: 2 }}>
              About Cosmos Shop
            </Typography>
            <Typography variant="body2" sx={{ color: 'rgba(255,255,255,0.7)' }}>
              Your one-stop destination for all things space-related. Discover telescopes,
              educational materials, and cosmic decor that brings the universe closer to home.
            </Typography>
          </Grid>
          <Grid item xs={12} sm={4}>
            <Typography variant="h6" sx={{ mb: 2 }}>
              Categories
            </Typography>
            <Grid container>
              {categories.map((category) => (
                <Grid item xs={6} key={category.name}>
                  <Link
                    component="button"
                    variant="body2"
                    onClick={() => handleCategoryClick(category.filter)}
                    sx={{
                      color: 'rgba(255,255,255,0.7)',
                      textDecoration: 'none',
                      display: 'block',
                      mb: 1,
                      textAlign: 'left',
                      '&:hover': {
                        color: '#7c4dff',
                      },
                    }}
                  >
                    {category.name}
                  </Link>
                </Grid>
              ))}
            </Grid>
          </Grid>
          <Grid item xs={12} sm={4}>
            <Typography variant="h6" sx={{ mb: 2 }}>
              Connect With Us
            </Typography>
            <Box sx={{ display: 'flex', gap: 1 }}>
              <IconButton
                sx={{
                  color: 'rgba(255,255,255,0.7)',
                  '&:hover': {
                    color: '#7c4dff',
                    transform: 'translateY(-2px)',
                  },
                  transition: 'all 0.3s ease-in-out',
                }}
              >
                <Facebook />
              </IconButton>
              <IconButton
                sx={{
                  color: 'rgba(255,255,255,0.7)',
                  '&:hover': {
                    color: '#7c4dff',
                    transform: 'translateY(-2px)',
                  },
                  transition: 'all 0.3s ease-in-out',
                }}
              >
                <Twitter />
              </IconButton>
              <IconButton
                sx={{
                  color: 'rgba(255,255,255,0.7)',
                  '&:hover': {
                    color: '#7c4dff',
                    transform: 'translateY(-2px)',
                  },
                  transition: 'all 0.3s ease-in-out',
                }}
              >
                <Instagram />
              </IconButton>
              <IconButton
                sx={{
                  color: 'rgba(255,255,255,0.7)',
                  '&:hover': {
                    color: '#7c4dff',
                    transform: 'translateY(-2px)',
                  },
                  transition: 'all 0.3s ease-in-out',
                }}
              >
                <LinkedIn />
              </IconButton>
              <IconButton
                sx={{
                  color: 'rgba(255,255,255,0.7)',
                  '&:hover': {
                    color: '#7c4dff',
                    transform: 'translateY(-2px)',
                  },
                  transition: 'all 0.3s ease-in-out',
                }}
              >
                <GitHub />
              </IconButton>
            </Box>
            <Typography variant="body2" sx={{ mt: 2, color: 'rgba(255,255,255,0.7)' }}>
              Contact us: support@cosmosshop.com
            </Typography>
          </Grid>
        </Grid>
        <Typography
          variant="body2"
          sx={{
            mt: 4,
            pt: 2,
            borderTop: '1px solid rgba(255,255,255,0.1)',
            textAlign: 'center',
            color: 'rgba(255,255,255,0.7)',
          }}
        >
          {new Date().getFullYear()} Cosmos Shop. All rights reserved.
        </Typography>
      </Container>
    </Box>
  );
};

export default Footer;
