import React from 'react';
import {
  Box,
  Container,
  Typography,
  Grid,
  Card,
  CardContent,
  CardMedia,
  Button,
  IconButton,
  TextField,
  Divider,
  Paper,
  Chip,
} from '@mui/material';
import {
  Add as AddIcon,
  Remove as RemoveIcon,
  Delete as DeleteIcon,
  ShoppingBag,
  LocalShipping,
  Verified,
} from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';
import { useProduct } from '../context/ProductContext';

const Cart = () => {
  const navigate = useNavigate();
  const { cart, removeFromCart, updateQuantity, getCartTotal } = useProduct();

  const handleQuantityChange = (productId, newQuantity) => {
    if (newQuantity >= 1) {
      updateQuantity(productId, newQuantity);
    }
  };

  const getCategoryInfo = (price) => {
    if (price >= 24999) return { label: 'Professional', color: '#FF6B6B' };
    if (price >= 7499) return { label: 'Advanced', color: '#00F5D4' };
    if (price >= 3999) return { label: 'Intermediate', color: '#FF9E00' };
    return { label: 'Beginner', color: '#00B6F1' };
  };

  return (
    <Box sx={{ py: 6, minHeight: '100vh' }}>
      <Container>
        <Typography
          variant="h3"
          component="h1"
          align="center"
          gutterBottom
          sx={{
            fontWeight: 700,
            background: 'linear-gradient(45deg, #00B6F1 30%, #4CDFFF 90%)',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            mb: 6,
          }}
        >
          Your Cosmic Cart
        </Typography>

        {cart.length === 0 ? (
          <Box
            sx={{
              textAlign: 'center',
              py: 8,
            }}
          >
            <ShoppingBag sx={{ fontSize: 64, color: '#00B6F1', mb: 2 }} />
            <Typography variant="h5" gutterBottom sx={{ color: '#00B6F1' }}>
              Your cart is empty
            </Typography>
            <Typography variant="body1" color="text.secondary" paragraph>
              Start exploring our collection of telescopes and space merchandise
            </Typography>
            <Button
              variant="contained"
              onClick={() => navigate('/')}
              sx={{
                minWidth: 200,
              }}
            >
              Explore Products
            </Button>
          </Box>
        ) : (
          <Grid container spacing={4}>
            <Grid item xs={12} md={8}>
              <Paper
                sx={{
                  p: 3,
                  position: 'relative',
                  overflow: 'hidden',
                  '&::before': {
                    content: '""',
                    position: 'absolute',
                    top: 0,
                    left: 0,
                    right: 0,
                    height: '4px',
                    background: 'linear-gradient(90deg, #00B6F1, #4CDFFF)',
                  },
                }}
              >
                {cart.map((item) => (
                  <Box key={item.id}>
                    <Grid container spacing={3} alignItems="center">
                      <Grid item xs={12} sm={3}>
                        <Box sx={{ position: 'relative' }}>
                          <CardMedia
                            component="img"
                            image={item.image}
                            alt={item.name}
                            sx={{
                              height: 120,
                              objectFit: 'cover',
                              borderRadius: 1,
                            }}
                          />
                          <Chip
                            label={getCategoryInfo(item.price).label}
                            sx={{
                              position: 'absolute',
                              top: 8,
                              right: 8,
                              bgcolor: getCategoryInfo(item.price).color,
                              color: 'white',
                              fontSize: '0.75rem',
                            }}
                          />
                        </Box>
                      </Grid>
                      <Grid item xs={12} sm={5}>
                        <Typography variant="h6" gutterBottom>
                          {item.name}
                        </Typography>
                        <Typography variant="body2" color="text.secondary" gutterBottom>
                          {item.description}
                        </Typography>
                        <Box sx={{ display: 'flex', gap: 1, mt: 1 }}>
                          <Chip
                            size="small"
                            icon={<LocalShipping sx={{ fontSize: '1rem' }} />}
                            label="Free Shipping"
                            sx={{
                              bgcolor: 'rgba(0, 245, 212, 0.1)',
                              color: '#00F5D4',
                              borderColor: '#00F5D4',
                            }}
                          />
                          <Chip
                            size="small"
                            icon={<Verified sx={{ fontSize: '1rem' }} />}
                            label="Genuine Product"
                            sx={{
                              bgcolor: 'rgba(0, 182, 241, 0.1)',
                              color: '#00B6F1',
                              borderColor: '#00B6F1',
                            }}
                          />
                        </Box>
                      </Grid>
                      <Grid item xs={12} sm={2}>
                        <Box sx={{ display: 'flex', alignItems: 'center' }}>
                          <IconButton
                            size="small"
                            onClick={() => handleQuantityChange(item.id, item.quantity - 1)}
                            sx={{
                              color: '#00B6F1',
                              '&:hover': {
                                bgcolor: 'rgba(0, 182, 241, 0.1)',
                              },
                            }}
                          >
                            <RemoveIcon />
                          </IconButton>
                          <TextField
                            size="small"
                            value={item.quantity}
                            onChange={(e) =>
                              handleQuantityChange(item.id, parseInt(e.target.value) || 0)
                            }
                            inputProps={{
                              min: 1,
                              style: { textAlign: 'center', width: '40px' },
                            }}
                            sx={{ mx: 1 }}
                          />
                          <IconButton
                            size="small"
                            onClick={() => handleQuantityChange(item.id, item.quantity + 1)}
                            sx={{
                              color: '#00B6F1',
                              '&:hover': {
                                bgcolor: 'rgba(0, 182, 241, 0.1)',
                              },
                            }}
                          >
                            <AddIcon />
                          </IconButton>
                        </Box>
                      </Grid>
                      <Grid item xs={12} sm={2}>
                        <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
                          <Typography variant="subtitle1" sx={{ fontWeight: 600 }}>
                            ₹{(item.price * item.quantity).toLocaleString()}
                          </Typography>
                          <IconButton
                            onClick={() => removeFromCart(item.id)}
                            sx={{
                              color: '#FF6B6B',
                              '&:hover': {
                                bgcolor: 'rgba(255, 107, 107, 0.1)',
                              },
                            }}
                          >
                            <DeleteIcon />
                          </IconButton>
                        </Box>
                      </Grid>
                    </Grid>
                    <Divider sx={{ my: 2 }} />
                  </Box>
                ))}
              </Paper>
            </Grid>
            <Grid item xs={12} md={4}>
              <Paper
                sx={{
                  p: 3,
                  position: 'sticky',
                  top: 24,
                  '&::before': {
                    content: '""',
                    position: 'absolute',
                    top: 0,
                    left: 0,
                    right: 0,
                    height: '4px',
                    background: 'linear-gradient(90deg, #FF6B6B, #FF9E00)',
                  },
                }}
              >
                <Typography variant="h6" gutterBottom>
                  Order Summary
                </Typography>
                <Box sx={{ my: 2 }}>
                  <Grid container justifyContent="space-between" sx={{ mb: 1 }}>
                    <Typography variant="body1">Subtotal</Typography>
                    <Typography variant="body1">₹{getCartTotal().toLocaleString()}</Typography>
                  </Grid>
                  <Grid container justifyContent="space-between" sx={{ mb: 1 }}>
                    <Typography variant="body1">Shipping</Typography>
                    <Typography variant="body1" sx={{ color: '#00F5D4' }}>
                      Free
                    </Typography>
                  </Grid>
                  <Divider sx={{ my: 2 }} />
                  <Grid container justifyContent="space-between" sx={{ mb: 3 }}>
                    <Typography variant="h6">Total</Typography>
                    <Typography variant="h6">₹{getCartTotal().toLocaleString()}</Typography>
                  </Grid>
                  <Button
                    fullWidth
                    variant="contained"
                    size="large"
                    onClick={() => navigate('/checkout')}
                    startIcon={<ShoppingBag />}
                  >
                    Proceed to Checkout
                  </Button>
                  <Box sx={{ mt: 3, p: 2, bgcolor: 'rgba(0, 245, 212, 0.1)', borderRadius: 1 }}>
                    <Typography variant="body2" align="center" sx={{ color: '#00F5D4' }}>
                      Free shipping on all orders
                    </Typography>
                  </Box>
                </Box>
              </Paper>
            </Grid>
          </Grid>
        )}
      </Container>
    </Box>
  );
};

export default Cart;
