import React from 'react';
import { Link } from 'react-router-dom';
import {
  Box,
  Typography,
  Button,
  Card,
  CardContent,
  IconButton,
  Grid,
  Divider,
  TextField,
} from '@mui/material';
import {
  Add as AddIcon,
  Remove as RemoveIcon,
  Delete as DeleteIcon,
  ShoppingCart as CartIcon,
} from '@mui/icons-material';
import { useProduct } from '../context/ProductContext';

const Cart = () => {
  const { cart, removeFromCart, updateQuantity, getCartTotal } = useProduct();

  if (cart.length === 0) {
    return (
      <Box
        sx={{
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          justifyContent: 'center',
          minHeight: '60vh',
          textAlign: 'center',
          p: 3,
        }}
      >
        <CartIcon sx={{ fontSize: 60, color: 'rgba(255,255,255,0.3)', mb: 2 }} />
        <Typography variant="h5" sx={{ mb: 2, color: 'white' }}>
          Your cart is empty
        </Typography>
        <Typography variant="body1" sx={{ mb: 3, color: 'rgba(255,255,255,0.7)' }}>
          Explore our collection and add some cosmic items!
        </Typography>
        <Button
          component={Link}
          to="/products"
          variant="contained"
          sx={{
            bgcolor: '#7c4dff',
            '&:hover': {
              bgcolor: '#6c3fff',
            },
          }}
        >
          Browse Products
        </Button>
      </Box>
    );
  }

  return (
    <Box sx={{ p: { xs: 2, md: 4 } }}>
      <Typography variant="h4" sx={{ mb: 4, color: 'white' }}>
        Shopping Cart
      </Typography>
      <Grid container spacing={4}>
        <Grid item xs={12} md={8}>
          {cart.map((item) => (
            <Card
              key={`${item.id}-${item.size}`}
              sx={{
                mb: 2,
                bgcolor: 'rgba(0,0,0,0.5)',
                backdropFilter: 'blur(10px)',
                border: '1px solid rgba(255,255,255,0.1)',
              }}
            >
              <CardContent>
                <Grid container spacing={2} alignItems="center">
                  <Grid item xs={12} sm={3}>
                    <img
                      src={item.image}
                      alt={item.name}
                      style={{
                        width: '100%',
                        height: 'auto',
                        borderRadius: '8px',
                      }}
                    />
                  </Grid>
                  <Grid item xs={12} sm={5}>
                    <Typography variant="h6" sx={{ color: 'white' }}>
                      {item.name}
                    </Typography>
                    <Typography
                      variant="body2"
                      sx={{ color: 'rgba(255,255,255,0.7)', mt: 1 }}
                    >
                      Size: {item.size}
                    </Typography>
                    <Typography
                      variant="body2"
                      sx={{ color: 'rgba(255,255,255,0.7)', mt: 1 }}
                    >
                      Price: ₹{item.price}
                    </Typography>
                  </Grid>
                  <Grid item xs={12} sm={4}>
                    <Box
                      sx={{
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'space-between',
                      }}
                    >
                      <Box sx={{ display: 'flex', alignItems: 'center' }}>
                        <IconButton
                          onClick={() =>
                            updateQuantity(item.id, item.size, item.quantity - 1)
                          }
                          sx={{ color: 'white' }}
                        >
                          <RemoveIcon />
                        </IconButton>
                        <TextField
                          value={item.quantity}
                          onChange={(e) => {
                            const value = parseInt(e.target.value) || 0;
                            updateQuantity(item.id, item.size, value);
                          }}
                          type="number"
                          InputProps={{
                            inputProps: { min: 1 },
                            sx: { 
                              color: 'white',
                              width: '60px',
                              '& .MuiOutlinedInput-notchedOutline': {
                                borderColor: 'rgba(255,255,255,0.3)',
                              },
                              '&:hover .MuiOutlinedInput-notchedOutline': {
                                borderColor: 'rgba(255,255,255,0.5)',
                              },
                            },
                          }}
                        />
                        <IconButton
                          onClick={() =>
                            updateQuantity(item.id, item.size, item.quantity + 1)
                          }
                          sx={{ color: 'white' }}
                        >
                          <AddIcon />
                        </IconButton>
                      </Box>
                      <IconButton
                        onClick={() => removeFromCart(item.id, item.size)}
                        sx={{ color: '#ff3d47' }}
                      >
                        <DeleteIcon />
                      </IconButton>
                    </Box>
                  </Grid>
                </Grid>
              </CardContent>
            </Card>
          ))}
        </Grid>
        <Grid item xs={12} md={4}>
          <Card
            sx={{
              position: 'sticky',
              top: '100px',
              bgcolor: 'rgba(0,0,0,0.5)',
              backdropFilter: 'blur(10px)',
              border: '1px solid rgba(255,255,255,0.1)',
            }}
          >
            <CardContent>
              <Typography variant="h6" sx={{ color: 'white', mb: 2 }}>
                Order Summary
              </Typography>
              <Box sx={{ mb: 2 }}>
                <Box
                  sx={{
                    display: 'flex',
                    justifyContent: 'space-between',
                    mb: 1,
                  }}
                >
                  <Typography sx={{ color: 'rgba(255,255,255,0.7)' }}>
                    Subtotal
                  </Typography>
                  <Typography sx={{ color: 'white' }}>
                    ₹{getCartTotal()}
                  </Typography>
                </Box>
                <Box
                  sx={{
                    display: 'flex',
                    justifyContent: 'space-between',
                    mb: 1,
                  }}
                >
                  <Typography sx={{ color: 'rgba(255,255,255,0.7)' }}>
                    Shipping
                  </Typography>
                  <Typography sx={{ color: 'white' }}>Free</Typography>
                </Box>
              </Box>
              <Divider sx={{ borderColor: 'rgba(255,255,255,0.1)', mb: 2 }} />
              <Box
                sx={{
                  display: 'flex',
                  justifyContent: 'space-between',
                  mb: 3,
                }}
              >
                <Typography variant="h6" sx={{ color: 'white' }}>
                  Total
                </Typography>
                <Typography variant="h6" sx={{ color: 'white' }}>
                  ₹{getCartTotal()}
                </Typography>
              </Box>
              <Button
                variant="contained"
                fullWidth
                sx={{
                  bgcolor: '#7c4dff',
                  '&:hover': {
                    bgcolor: '#6c3fff',
                  },
                }}
              >
                Proceed to Checkout
              </Button>
            </CardContent>
          </Card>
        </Grid>
      </Grid>
    </Box>
  );
};

export default Cart;
