import React, { createContext, useContext, useState, useEffect } from 'react';

const ProductContext = createContext();

export const useProduct = () => {
  return useContext(ProductContext);
};

export const ProductProvider = ({ children }) => {
  const [cart, setCart] = useState([]);
  const [wishlist, setWishlist] = useState([]);

  // Load cart and wishlist from localStorage on mount
  useEffect(() => {
    const savedCart = localStorage.getItem('cosmosCart');
    const savedWishlist = localStorage.getItem('cosmosWishlist');
    if (savedCart) setCart(JSON.parse(savedCart));
    if (savedWishlist) setWishlist(JSON.parse(savedWishlist));
  }, []);

  // Save cart and wishlist to localStorage whenever they change
  useEffect(() => {
    localStorage.setItem('cosmosCart', JSON.stringify(cart));
  }, [cart]);

  useEffect(() => {
    localStorage.setItem('cosmosWishlist', JSON.stringify(wishlist));
  }, [wishlist]);

  const addToCart = (product) => {
    setCart((prevCart) => {
      const existingItem = prevCart.find((item) => item.id === product.id);
      if (existingItem) {
        return prevCart.map((item) =>
          item.id === product.id
            ? { ...item, quantity: item.quantity + 1 }
            : item
        );
      }
      return [...prevCart, { ...product, quantity: 1 }];
    });
  };

  const removeFromCart = (productId) => {
    setCart((prevCart) => prevCart.filter((item) => item.id !== productId));
  };

  const updateQuantity = (productId, quantity) => {
    if (quantity < 1) {
      removeFromCart(productId);
      return;
    }
    setCart((prevCart) =>
      prevCart.map((item) =>
        item.id === productId ? { ...item, quantity } : item
      )
    );
  };

  const addToWishlist = (product) => {
    setWishlist((prevWishlist) => {
      if (!prevWishlist.find((item) => item.id === product.id)) {
        return [...prevWishlist, product];
      }
      return prevWishlist;
    });
  };

  const removeFromWishlist = (productId) => {
    setWishlist((prevWishlist) =>
      prevWishlist.filter((item) => item.id !== productId)
    );
  };

  const moveToCart = (productId) => {
    const product = wishlist.find((item) => item.id === productId);
    if (product) {
      addToCart(product);
      removeFromWishlist(productId);
    }
  };

  const getCartTotal = () => {
    return cart.reduce((total, item) => total + item.price * item.quantity, 0);
  };

  const getCategoryInfo = (price) => {
    if (price >= 24999) {
      return {
        label: 'Professional',
        color: '#FF6B6B',
        description: 'For professional astronomers and advanced enthusiasts',
        features: ['High-precision optics', 'Motor drive system', 'Advanced tracking'],
      };
    }
    if (price >= 7499) {
      return {
        label: 'Advanced',
        color: '#00F5D4',
        description: 'For serious astronomy enthusiasts',
        features: ['Quality optics', 'Stable mount', 'Multiple eyepieces'],
      };
    }
    if (price >= 3999) {
      return {
        label: 'Intermediate',
        color: '#FF9E00',
        description: 'For hobbyists and learners',
        features: ['Good optics', 'Basic mount', 'Standard eyepiece'],
      };
    }
    return {
      label: 'Beginner',
      color: '#00B6F1',
      description: 'Perfect for starting your cosmic journey',
      features: ['Easy setup', 'Lightweight', 'Basic features'],
    };
  };

  const clearCart = () => {
    setCart([]);
  };

  const value = {
    cart,
    wishlist,
    addToCart,
    removeFromCart,
    updateQuantity,
    addToWishlist,
    removeFromWishlist,
    moveToCart,
    getCartTotal,
    getCategoryInfo,
    clearCart,
  };

  return (
    <ProductContext.Provider value={value}>
      {children}
    </ProductContext.Provider>
  );
};

export default ProductContext;
