const categories = [
  'All',
  'Telescopes & Accessories',
  'Space Merchandise',
  'Books & Education',
  'Collectibles',
  'Gadgets & Kits',
  'Home & Lifestyle',
];

const subCategories = {
  'Space Merchandise': ['All', 'Men', 'Women', 'Kids'],
};

const sortOptions = [
  { value: 'recommended', label: 'Recommended' },
  { value: 'newest', label: 'What\'s New' },
  { value: 'price_low', label: 'Price: Low to High' },
  { value: 'price_high', label: 'Price: High to Low' },
  { value: 'discount', label: 'Better Discount' },
  { value: 'rating', label: 'Customer Rating' },
];

const Products = () => {
  const location = useLocation();
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [selectedSubCategory, setSelectedSubCategory] = useState('All');
  const [priceRange, setPriceRange] = useState([0, 35000]);
  const [sortBy, setSortBy] = useState('recommended');
  const [selectedSizes, setSelectedSizes] = useState([]);
  const [wishlist, setWishlist] = useState([]);

  useEffect(() => {
    if (location.state?.category) {
      setSelectedCategory(location.state.category);
    }
  }, [location.state]);

  const handleSortChange = (event) => {
    setSortBy(event.target.value);
  };

  const handleSizeToggle = (size) => {
    setSelectedSizes((prev) =>
      prev.includes(size)
        ? prev.filter((s) => s !== size)
        : [...prev, size]
    );
  };

  const toggleWishlist = (productId) => {
    setWishlist((prev) =>
      prev.includes(productId)
        ? prev.filter((id) => id !== productId)
        : [...prev, productId]
    );
  };

  let filteredProducts = products.filter((product) => {
    const matchesSearch = product.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
      product.description.toLowerCase().includes(searchQuery.toLowerCase());
    const matchesCategory = selectedCategory === 'All' || product.category === selectedCategory;
    const matchesSubCategory = selectedSubCategory === 'All' || product.subCategory === selectedSubCategory;
    const matchesPrice = product.price >= priceRange[0] && product.price <= priceRange[1];
    const matchesSize = selectedSizes.length === 0 || product.sizes.some(size => selectedSizes.includes(size));
    return matchesSearch && matchesCategory && matchesSubCategory && matchesPrice && matchesSize;
  });

  // Sort products
  filteredProducts = filteredProducts.sort((a, b) => {
    switch (sortBy) {
      case 'price_low':
        return a.price - b.price;
      case 'price_high':
        return b.price - a.price;
      case 'discount':
        return b.discount - a.discount;
      case 'rating':
        return b.rating - a.rating;
      default:
        return 0;
    }
  });

  return (
    <Container maxWidth="lg" sx={{ py: 8 }}>
      {/* Filters Section */}
      <Box
        sx={{
          mb: 6,
          p: 3,
          borderRadius: 2,
          background: 'rgba(255,255,255,0.05)',
          backdropFilter: 'blur(10px)',
          border: '1px solid rgba(255,255,255,0.1)',
        }}
      >
        <Grid container spacing={3} alignItems="center">
          <Grid item xs={12} sm={4}>
            <TextField
              fullWidth
              variant="outlined"
              placeholder="Search products..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <SearchIcon sx={{ color: 'rgba(255,255,255,0.7)' }} />
                  </InputAdornment>
                ),
                sx: {
                  color: 'white',
                  '& .MuiOutlinedInput-notchedOutline': {
                    borderColor: 'rgba(255,255,255,0.23)',
                  },
                  '&:hover .MuiOutlinedInput-notchedOutline': {
                    borderColor: 'rgba(255,255,255,0.4)',
                  },
                  '&.Mui-focused .MuiOutlinedInput-notchedOutline': {
                    borderColor: '#7c4dff',
                  },
                },
              }}
            />
          </Grid>
          <Grid item xs={12} sm={4}>
            <FormControl fullWidth>
              <InputLabel sx={{ color: 'rgba(255,255,255,0.7)' }}>
                Category
              </InputLabel>
              <Select
                value={selectedCategory}
                onChange={(e) => setSelectedCategory(e.target.value)}
                label="Category"
                sx={{
                  color: 'white',
                  '& .MuiOutlinedInput-notchedOutline': {
                    borderColor: 'rgba(255,255,255,0.23)',
                  },
                }}
              >
                {categories.map((category) => (
                  <MenuItem key={category} value={category}>
                    {category}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>
          <Grid item xs={12} sm={4}>
            <FormControl fullWidth>
              <InputLabel sx={{ color: 'rgba(255,255,255,0.7)' }}>
                Sort By
              </InputLabel>
              <Select
                value={sortBy}
                onChange={handleSortChange}
                label="Sort By"
                sx={{
                  color: 'white',
                  '& .MuiOutlinedInput-notchedOutline': {
                    borderColor: 'rgba(255,255,255,0.23)',
                  },
                }}
              >
                {sortOptions.map((option) => (
                  <MenuItem key={option.value} value={option.value}>
                    {option.label}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>
          {selectedCategory === 'Space Merchandise' && (
            <Grid item xs={12}>
              <Typography variant="subtitle1" sx={{ color: 'white', mb: 1 }}>
                Sub Category
              </Typography>
              <ToggleButtonGroup
                value={selectedSubCategory}
                exclusive
                onChange={(e, value) => setSelectedSubCategory(value || 'All')}
                sx={{ bgcolor: 'rgba(255,255,255,0.05)' }}
              >
                {subCategories['Space Merchandise'].map((subCat) => (
                  <ToggleButton
                    key={subCat}
                    value={subCat}
                    sx={{
                      color: 'white',
                      '&.Mui-selected': {
                        color: '#7c4dff',
                        bgcolor: 'rgba(124,77,255,0.1)',
                      },
                    }}
                  >
                    {subCat}
                  </ToggleButton>
                ))}
              </ToggleButtonGroup>
            </Grid>
          )}
        </Grid>
      </Box>

      {/* Products Grid */}
      <Grid container spacing={3}>
        {filteredProducts.map((product) => (
          <Grid item key={product.id} xs={12} sm={6} md={4}>
            <Card
              sx={{
                height: '100%',
                display: 'flex',
                flexDirection: 'column',
                background: 'rgba(255,255,255,0.05)',
                backdropFilter: 'blur(10px)',
                border: '1px solid rgba(255,255,255,0.1)',
                transition: 'all 0.3s ease-in-out',
                '&:hover': {
                  transform: 'translateY(-4px)',
                  boxShadow: '0 8px 16px rgba(0,0,0,0.2)',
                  '& .MuiCardMedia-root': {
                    transform: 'scale(1.05)',
                  },
                },
              }}
            >
              <Box sx={{ position: 'relative', pt: '133%', overflow: 'hidden' }}>
                <CardMedia
                  component="img"
                  image={product.image}
                  alt={product.name}
                  sx={{
                    position: 'absolute',
                    top: 0,
                    left: 0,
                    width: '100%',
                    height: '100%',
                    transition: 'transform 0.3s ease-in-out',
                  }}
                />
                <IconButton
                  onClick={() => toggleWishlist(product.id)}
                  sx={{
                    position: 'absolute',
                    top: 8,
                    right: 8,
                    bgcolor: 'rgba(255,255,255,0.9)',
                    '&:hover': { bgcolor: 'white' },
                  }}
                >
                  {wishlist.includes(product.id) ? (
                    <Favorite sx={{ color: '#ff3d47' }} />
                  ) : (
                    <FavoriteBorder />
                  )}
                </IconButton>
              </Box>
              <CardContent sx={{ flexGrow: 1 }}>
                <Typography
                  variant="subtitle1"
                  sx={{
                    color: 'white',
                    fontWeight: 600,
                    mb: 0.5,
                  }}
                >
                  {product.brand}
                </Typography>
                <Typography
                  variant="body2"
                  sx={{
                    color: 'rgba(255,255,255,0.7)',
                    mb: 1,
                    height: '40px',
                    overflow: 'hidden',
                    textOverflow: 'ellipsis',
                    display: '-webkit-box',
                    WebkitLineClamp: 2,
                    WebkitBoxOrient: 'vertical',
                  }}
                >
                  {product.name}
                </Typography>
                <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                  <Typography
                    variant="h6"
                    component="span"
                    sx={{ color: 'white', mr: 1 }}
                  >
                    ₹{product.price}
                  </Typography>
                  <Typography
                    variant="body2"
                    component="span"
                    sx={{
                      color: 'rgba(255,255,255,0.5)',
                      textDecoration: 'line-through',
                      mr: 1,
                    }}
                  >
                    ₹{product.originalPrice}
                  </Typography>
                  <Typography
                    variant="body2"
                    component="span"
                    sx={{ color: '#00b852' }}
                  >
                    ({product.discount}% OFF)
                  </Typography>
                </Box>
                <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                  <Rating
                    value={product.rating}
                    precision={0.1}
                    readOnly
                    size="small"
                    sx={{
                      color: '#7c4dff',
                      mr: 1,
                    }}
                  />
                  <Typography
                    variant="body2"
                    sx={{ color: 'rgba(255,255,255,0.7)' }}
                  >
                    ({product.reviews})
                  </Typography>
                </Box>
                <Box sx={{ mb: 2 }}>
                  <Typography
                    variant="body2"
                    sx={{ color: 'white', mb: 0.5 }}
                  >
                    Sizes:
                  </Typography>
                  <Box sx={{ display: 'flex', gap: 0.5 }}>
                    {product.sizes.map((size) => (
                      <Chip
                        key={size}
                        label={size}
                        onClick={() => handleSizeToggle(size)}
                        sx={{
                          bgcolor: selectedSizes.includes(size)
                            ? '#7c4dff'
                            : 'rgba(255,255,255,0.05)',
                          color: selectedSizes.includes(size)
                            ? 'white'
                            : 'rgba(255,255,255,0.7)',
                          '&:hover': {
                            bgcolor: selectedSizes.includes(size)
                              ? '#6c3fff'
                              : 'rgba(255,255,255,0.1)',
                          },
                        }}
                      />
                    ))}
                  </Box>
                </Box>
                {product.inStock && (
                  <Box
                    sx={{
                      display: 'flex',
                      alignItems: 'center',
                      color: '#00b852',
                      gap: 0.5,
                    }}
                  >
                    <LocalShipping fontSize="small" />
                    <Typography variant="body2">
                      Free Delivery
                    </Typography>
                  </Box>
                )}
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
};

export default Products;
