import React, { useState } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import {
  AppBar,
  Box,
  Toolbar,
  IconButton,
  Typography,
  Button,
  Drawer,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  Badge,
  useMediaQuery,
  useTheme,
  Menu,
  MenuItem,
  Collapse,
  ListItemButton,
} from '@mui/material';
import {
  Menu as MenuIcon,
  ShoppingCart as CartIcon,
  Home as HomeIcon,
  Category as ProductsIcon,
  Close as CloseIcon,
  Favorite as FavoriteIcon,
  ExpandLess,
  ExpandMore,
  CameraAlt,
  LocalShipping,
  MenuBook,
  Science,
  Toys,
  HomeWork,
  Code,
} from '@mui/icons-material';
import { useProduct } from '../context/ProductContext';

const categories = [
  { name: 'Telescopes & Accessories', icon: <CameraAlt />, path: '/products?category=telescopes' },
  { name: 'Space Merchandise', icon: <LocalShipping />, path: '/products?category=merchandise' },
  { name: 'Books & Education', icon: <MenuBook />, path: '/products?category=books' },
  { name: 'Collectibles', icon: <Science />, path: '/products?category=collectibles' },
  { name: 'Gadgets & Kits', icon: <Toys />, path: '/products?category=gadgets' },
  { name: 'Home & Lifestyle', icon: <HomeWork />, path: '/products?category=home' },
  { name: 'Software & Apps', icon: <Code />, path: '/products?category=software' },
];

const Navbar = () => {
  const [mobileOpen, setMobileOpen] = useState(false);
  const [categoryMenuAnchor, setCategoryMenuAnchor] = useState(null);
  const [categoryOpen, setCategoryOpen] = useState(false);
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));
  const navigate = useNavigate();
  const location = useLocation();
  const { getCartCount, wishlist } = useProduct();

  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };

  const handleCategoryClick = (event) => {
    if (!isMobile) {
      setCategoryMenuAnchor(event.currentTarget);
    } else {
      setCategoryOpen(!categoryOpen);
    }
  };

  const handleCategoryClose = () => {
    setCategoryMenuAnchor(null);
  };

  const handleCategorySelect = (path) => {
    navigate(path);
    handleCategoryClose();
    if (isMobile) {
      setMobileOpen(false);
    }
  };

  const pages = [
    { name: 'Home', path: '/' },
    { name: 'Cart', path: '/cart' },
  ];

  const drawer = (
    <Box sx={{ width: 280, bgcolor: 'background.paper' }}>
      <Box
        sx={{
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'space-between',
          p: 2,
          borderBottom: '1px solid rgba(255,255,255,0.1)',
        }}
      >
        <Typography variant="h6" sx={{ color: 'primary.main', fontWeight: 'bold' }}>
          Cosmos Shop
        </Typography>
        <IconButton onClick={handleDrawerToggle} sx={{ color: 'primary.main' }}>
          <CloseIcon />
        </IconButton>
      </Box>
      <List>
        {pages.map((page) => (
          <ListItem
            key={page.name}
            button
            component={Link}
            to={page.path}
            onClick={handleDrawerToggle}
            selected={location.pathname === page.path}
            sx={{
              '&.Mui-selected': {
                bgcolor: 'rgba(0, 180, 216, 0.1)',
                '&:hover': {
                  bgcolor: 'rgba(0, 180, 216, 0.2)',
                },
              },
            }}
          >
            <ListItemIcon sx={{ color: 'primary.main' }}>
              {page.name === 'Home' ? <HomeIcon /> : <CartIcon />}
            </ListItemIcon>
            <ListItemText 
              primary={page.name}
              sx={{ 
                '& .MuiTypography-root': {
                  color: 'text.primary',
                }
              }}
            />
          </ListItem>
        ))}
      </List>
    </Box>
  );

  return (
    <AppBar
      position="sticky"
      sx={{
        background: 'rgba(3, 7, 30, 0.8)',
        backdropFilter: 'blur(10px)',
        borderBottom: '1px solid rgba(255,255,255,0.1)',
      }}
    >
      <Toolbar sx={{ justifyContent: 'space-between' }}>
        <Box sx={{ display: 'flex', alignItems: 'center' }}>
          {isMobile && (
            <IconButton
              color="inherit"
              aria-label="open drawer"
              edge="start"
              onClick={handleDrawerToggle}
              sx={{
                mr: 2,
                color: 'primary.main',
              }}
            >
              <MenuIcon />
            </IconButton>
          )}
          <Typography
            variant="h6"
            component={Link}
            to="/"
            sx={{
              textDecoration: 'none',
              color: 'primary.main',
              fontWeight: 700,
              letterSpacing: 1,
              mr: 4,
            }}
          >
            Cosmos Shop
          </Typography>
          {!isMobile && (
            <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
              {pages.map((page) => (
                <Button
                  key={page.name}
                  component={Link}
                  to={page.path}
                  startIcon={page.name === 'Home' ? <HomeIcon /> : <CartIcon />}
                  sx={{
                    color: location.pathname === page.path ? 'primary.main' : 'text.primary',
                    '&:hover': {
                      color: 'primary.main',
                    },
                  }}
                >
                  {page.name}
                </Button>
              ))}
            </Box>
          )}
        </Box>
        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
          <IconButton
            onClick={() => navigate('/products')}
            sx={{
              color: 'primary.main',
            }}
          >
            <Badge 
              badgeContent={wishlist.length} 
              color="error"
              sx={{
                '& .MuiBadge-badge': {
                  bgcolor: theme.palette.cosmic.nebula,
                },
              }}
            >
              <FavoriteIcon />
            </Badge>
          </IconButton>
          <IconButton
            onClick={() => navigate('/cart')}
            sx={{
              color: 'primary.main',
            }}
          >
            <Badge 
              badgeContent={getCartCount()} 
              color="error"
              sx={{
                '& .MuiBadge-badge': {
                  bgcolor: theme.palette.cosmic.meteor,
                },
              }}
            >
              <CartIcon />
            </Badge>
          </IconButton>
        </Box>
      </Toolbar>
      <Drawer
        variant="temporary"
        anchor="left"
        open={mobileOpen}
        onClose={handleDrawerToggle}
        ModalProps={{
          keepMounted: true,
        }}
        PaperProps={{
          sx: {
            width: 280,
            bgcolor: 'background.paper',
            backgroundImage: 'none',
          },
        }}
      >
        {drawer}
      </Drawer>
    </AppBar>
  );
};

export default Navbar;
