import React, { useState } from 'react';
import { Link as RouterLink } from 'react-router-dom';
import {
  AppBar,
  Toolbar,
  Typography,
  Button,
  IconButton,
  Badge,
  Box,
  Menu,
  MenuItem,
  Container,
} from '@mui/material';
import {
  ShoppingCart,
  Menu as MenuIcon,
  Science,
} from '@mui/icons-material';

const Navbar = () => {
  const [mobileMenu, setMobileMenu] = useState(null);

  const handleMobileMenuOpen = (event) => {
    setMobileMenu(event.currentTarget);
  };

  const handleMobileMenuClose = () => {
    setMobileMenu(null);
  };

  return (
    <AppBar 
      position="fixed" 
      sx={{ 
        background: 'rgba(0, 0, 0, 0.8)', 
        backdropFilter: 'blur(8px)',
        borderBottom: '1px solid rgba(255,255,255,0.1)',
      }}
    >
      <Container maxWidth="lg">
        <Toolbar disableGutters>
          <IconButton
            edge="start"
            color="inherit"
            sx={{ 
              display: { sm: 'none' }, 
              mr: 2,
              '&:hover': {
                background: 'rgba(255,255,255,0.1)',
              },
            }}
            onClick={handleMobileMenuOpen}
          >
            <MenuIcon />
          </IconButton>

          <Science 
            sx={{ 
              mr: 2,
              color: '#7c4dff',
              fontSize: '2rem',
            }} 
          />
          <Typography
            variant="h6"
            component={RouterLink}
            to="/"
            sx={{
              flexGrow: 1,
              textDecoration: 'none',
              color: 'inherit',
              fontWeight: 700,
              fontSize: { xs: '1.2rem', sm: '1.5rem' },
              background: 'linear-gradient(45deg, #7c4dff, #1a237e)',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
              letterSpacing: '0.5px',
            }}
          >
            Cosmos Shop
          </Typography>

          <Box sx={{ display: { xs: 'none', sm: 'flex' }, alignItems: 'center' }}>
            <Button 
              color="inherit" 
              component={RouterLink} 
              to="/"
              sx={{
                mx: 1,
                '&:hover': {
                  background: 'rgba(255,255,255,0.1)',
                },
              }}
            >
              Home
            </Button>
            <Button 
              color="inherit" 
              component={RouterLink} 
              to="/products"
              sx={{
                mx: 1,
                '&:hover': {
                  background: 'rgba(255,255,255,0.1)',
                },
              }}
            >
              Products
            </Button>
            <IconButton 
              color="inherit" 
              component={RouterLink} 
              to="/cart"
              sx={{
                ml: 1,
                '&:hover': {
                  background: 'rgba(255,255,255,0.1)',
                },
              }}
            >
              <Badge 
                badgeContent={0} 
                color="secondary"
                sx={{
                  '& .MuiBadge-badge': {
                    background: '#7c4dff',
                  },
                }}
              >
                <ShoppingCart />
              </Badge>
            </IconButton>
          </Box>

          <Menu
            anchorEl={mobileMenu}
            open={Boolean(mobileMenu)}
            onClose={handleMobileMenuClose}
            PaperProps={{
              sx: {
                background: 'rgba(0, 0, 0, 0.9)',
                backdropFilter: 'blur(8px)',
                border: '1px solid rgba(255,255,255,0.1)',
                mt: 5,
              },
            }}
            transformOrigin={{ horizontal: 'left', vertical: 'top' }}
            anchorOrigin={{ horizontal: 'left', vertical: 'bottom' }}
          >
            <MenuItem 
              component={RouterLink} 
              to="/" 
              onClick={handleMobileMenuClose}
              sx={{
                '&:hover': {
                  background: 'rgba(255,255,255,0.1)',
                },
              }}
            >
              Home
            </MenuItem>
            <MenuItem 
              component={RouterLink} 
              to="/products" 
              onClick={handleMobileMenuClose}
              sx={{
                '&:hover': {
                  background: 'rgba(255,255,255,0.1)',
                },
              }}
            >
              Products
            </MenuItem>
            <MenuItem 
              component={RouterLink} 
              to="/cart" 
              onClick={handleMobileMenuClose}
              sx={{
                '&:hover': {
                  background: 'rgba(255,255,255,0.1)',
                },
              }}
            >
              Cart
            </MenuItem>
          </Menu>
        </Toolbar>
      </Container>
    </AppBar>
  );
};

export default Navbar;
