import React, { useState } from 'react';
import {
  Box,
  Container,
  Typography,
  Grid,
  Paper,
  TextField,
  Button,
  Stepper,
  Step,
  StepLabel,
  Radio,
  RadioGroup,
  FormControlLabel,
  FormControl,
  Divider,
  Card,
  CardContent,
} from '@mui/material';
import {
  LocalShipping,
  Payment,
  CreditCard,
  AccountBalance,
  PhoneAndroid,
} from '@mui/icons-material';
import { useProduct } from '../context/ProductContext';
import { useNavigate } from 'react-router-dom';

const steps = ['Address', 'Payment', 'Review'];

const paymentMethods = [
  {
    id: 'card',
    name: 'Credit/Debit Card',
    icon: <CreditCard />,
    description: 'Pay securely with your card',
  },
  {
    id: 'upi',
    name: 'UPI',
    icon: <PhoneAndroid />,
    description: 'Pay using any UPI app',
  },
  {
    id: 'netbanking',
    name: 'Net Banking',
    icon: <AccountBalance />,
    description: 'Pay through your bank account',
  },
];

const Checkout = () => {
  const navigate = useNavigate();
  const { cart, getCartTotal } = useProduct();
  const [activeStep, setActiveStep] = useState(0);
  const [paymentMethod, setPaymentMethod] = useState('card');
  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    phone: '',
    address: '',
    city: '',
    state: '',
    pincode: '',
    cardNumber: '',
    cardName: '',
    expiryDate: '',
    cvv: '',
    upiId: '',
    bank: '',
  });

  const handleNext = () => {
    setActiveStep((prevStep) => prevStep + 1);
  };

  const handleBack = () => {
    setActiveStep((prevStep) => prevStep - 1);
  };

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handlePaymentMethodChange = (event) => {
    setPaymentMethod(event.target.value);
  };

  const handlePlaceOrder = () => {
    // Here you would typically make an API call to process the payment
    // For now, we'll just simulate a successful payment
    setTimeout(() => {
      navigate('/order-success');
    }, 2000);
  };

  const renderAddressForm = () => (
    <Grid container spacing={3}>
      <Grid item xs={12} sm={6}>
        <TextField
          required
          fullWidth
          label="Full Name"
          name="fullName"
          value={formData.fullName}
          onChange={handleInputChange}
        />
      </Grid>
      <Grid item xs={12} sm={6}>
        <TextField
          required
          fullWidth
          label="Email"
          name="email"
          type="email"
          value={formData.email}
          onChange={handleInputChange}
        />
      </Grid>
      <Grid item xs={12}>
        <TextField
          required
          fullWidth
          label="Phone Number"
          name="phone"
          value={formData.phone}
          onChange={handleInputChange}
        />
      </Grid>
      <Grid item xs={12}>
        <TextField
          required
          fullWidth
          label="Address"
          name="address"
          multiline
          rows={3}
          value={formData.address}
          onChange={handleInputChange}
        />
      </Grid>
      <Grid item xs={12} sm={6}>
        <TextField
          required
          fullWidth
          label="City"
          name="city"
          value={formData.city}
          onChange={handleInputChange}
        />
      </Grid>
      <Grid item xs={12} sm={6}>
        <TextField
          required
          fullWidth
          label="State"
          name="state"
          value={formData.state}
          onChange={handleInputChange}
        />
      </Grid>
      <Grid item xs={12} sm={6}>
        <TextField
          required
          fullWidth
          label="PIN Code"
          name="pincode"
          value={formData.pincode}
          onChange={handleInputChange}
        />
      </Grid>
    </Grid>
  );

  const renderPaymentForm = () => (
    <Box>
      <FormControl component="fieldset" sx={{ width: '100%', mb: 3 }}>
        <RadioGroup
          value={paymentMethod}
          onChange={handlePaymentMethodChange}
        >
          {paymentMethods.map((method) => (
            <Paper
              key={method.id}
              sx={{
                mb: 2,
                border: paymentMethod === method.id ? '2px solid #2196F3' : '1px solid #e0e0e0',
                borderRadius: 2,
              }}
            >
              <FormControlLabel
                value={method.id}
                control={<Radio />}
                label={
                  <Box sx={{ display: 'flex', alignItems: 'center', p: 1 }}>
                    <Box sx={{ mr: 2 }}>{method.icon}</Box>
                    <Box>
                      <Typography variant="subtitle1">{method.name}</Typography>
                      <Typography variant="body2" color="text.secondary">
                        {method.description}
                      </Typography>
                    </Box>
                  </Box>
                }
                sx={{ width: '100%', m: 0 }}
              />
            </Paper>
          ))}
        </RadioGroup>
      </FormControl>

      {paymentMethod === 'card' && (
        <Grid container spacing={3}>
          <Grid item xs={12}>
            <TextField
              required
              fullWidth
              label="Card Number"
              name="cardNumber"
              value={formData.cardNumber}
              onChange={handleInputChange}
            />
          </Grid>
          <Grid item xs={12}>
            <TextField
              required
              fullWidth
              label="Name on Card"
              name="cardName"
              value={formData.cardName}
              onChange={handleInputChange}
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              required
              fullWidth
              label="Expiry Date"
              name="expiryDate"
              placeholder="MM/YY"
              value={formData.expiryDate}
              onChange={handleInputChange}
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              required
              fullWidth
              label="CVV"
              name="cvv"
              type="password"
              value={formData.cvv}
              onChange={handleInputChange}
            />
          </Grid>
        </Grid>
      )}

      {paymentMethod === 'upi' && (
        <TextField
          required
          fullWidth
          label="UPI ID"
          name="upiId"
          placeholder="username@upi"
          value={formData.upiId}
          onChange={handleInputChange}
        />
      )}

      {paymentMethod === 'netbanking' && (
        <TextField
          required
          fullWidth
          select
          label="Select Bank"
          name="bank"
          value={formData.bank}
          onChange={handleInputChange}
          SelectProps={{
            native: true,
          }}
        >
          <option value="">Select a bank</option>
          <option value="sbi">State Bank of India</option>
          <option value="hdfc">HDFC Bank</option>
          <option value="icici">ICICI Bank</option>
          <option value="axis">Axis Bank</option>
        </TextField>
      )}
    </Box>
  );

  const renderReview = () => (
    <Grid container spacing={3}>
      <Grid item xs={12}>
        <Typography variant="h6" gutterBottom>
          Order Summary
        </Typography>
        {cart.map((item) => (
          <Box key={item.id} sx={{ mb: 2 }}>
            <Grid container spacing={2} alignItems="center">
              <Grid item xs={2}>
                <img
                  src={item.image}
                  alt={item.name}
                  style={{
                    width: '100%',
                    height: 'auto',
                    borderRadius: '8px',
                  }}
                />
              </Grid>
              <Grid item xs={7}>
                <Typography variant="subtitle1">{item.name}</Typography>
                <Typography variant="body2" color="text.secondary">
                  Quantity: {item.quantity}
                </Typography>
              </Grid>
              <Grid item xs={3}>
                <Typography variant="subtitle1" align="right">
                  ₹{(item.price * item.quantity).toLocaleString()}
                </Typography>
              </Grid>
            </Grid>
          </Box>
        ))}
        <Divider sx={{ my: 2 }} />
        <Grid container justifyContent="space-between">
          <Typography variant="h6">Total</Typography>
          <Typography variant="h6">₹{getCartTotal().toLocaleString()}</Typography>
        </Grid>
      </Grid>
    </Grid>
  );

  return (
    <Box sx={{ py: 6, minHeight: '100vh', bgcolor: 'grey.50' }}>
      <Container>
        <Paper
          sx={{
            p: 4,
            mb: 4,
            borderRadius: 2,
            boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
          }}
        >
          <Stepper activeStep={activeStep} sx={{ mb: 4 }}>
            {steps.map((label) => (
              <Step key={label}>
                <StepLabel>{label}</StepLabel>
              </Step>
            ))}
          </Stepper>

          <Box sx={{ mt: 4 }}>
            {activeStep === 0 && renderAddressForm()}
            {activeStep === 1 && renderPaymentForm()}
            {activeStep === 2 && renderReview()}

            <Box sx={{ display: 'flex', justifyContent: 'flex-end', mt: 4 }}>
              {activeStep !== 0 && (
                <Button onClick={handleBack} sx={{ mr: 1 }}>
                  Back
                </Button>
              )}
              <Button
                variant="contained"
                onClick={activeStep === steps.length - 1 ? handlePlaceOrder : handleNext}
                sx={{
                  background: 'linear-gradient(45deg, #2196F3 30%, #21CBF3 90%)',
                  color: 'white',
                  '&:hover': {
                    background: 'linear-gradient(45deg, #21CBF3 30%, #2196F3 90%)',
                  },
                }}
              >
                {activeStep === steps.length - 1 ? 'Place Order' : 'Next'}
              </Button>
            </Box>
          </Box>
        </Paper>
      </Container>
    </Box>
  );
};

export default Checkout;
