import React, { useState } from 'react';
import {
  Container,
  Grid,
  Card,
  CardMedia,
  CardContent,
  CardActions,
  Typography,
  Button,
  Box,
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Slider,
  InputAdornment,
} from '@mui/material';
import { Search as SearchIcon } from '@mui/icons-material';

const products = [
  {
    id: 1,
    name: 'Matrix Phoenix Refractor Telescope',
    description: 'High-quality 60/700 refractor telescope with aluminum tripod, perfect for beginners. Includes 1.25" eyepieces and finder scope.',
    price: 3999.00,
    category: 'Equipment',
    image: 'https://m.media-amazon.com/images/I/71+-gs+VLWL._SX522_.jpg',
  },
  {
    id: 2,
    name: 'Celestron AstroMaster Motor Drive Telescope',
    description: 'Professional telescope with motor drive for automatic celestial tracking. Perfect for deep space observation.',
    price: 32999.00,
    category: 'Equipment',
    image: 'https://m.media-amazon.com/images/I/61vRNTZaAIL._SX522_.jpg',
  },
  {
    id: 3,
    name: 'SSEA Professional Astronomical Telescope',
    description: '70mm aperture telescope with high magnification, tripod mount, and phone adapter for astrophotography.',
    price: 7499.00,
    category: 'Equipment',
    image: 'https://m.media-amazon.com/images/I/71K4ZQKZDkL._SX522_.jpg',
  },
  {
    id: 4,
    name: 'Astronomical Reflector Telescope',
    description: 'Advanced reflector telescope with 114mm aperture, includes motor drive and multiple eyepieces for detailed sky observation.',
    price: 15999.00,
    category: 'Equipment',
    image: 'https://m.media-amazon.com/images/I/71dqjrzQ5eL._SX522_.jpg',
  },
];

const categories = ['All', 'Equipment'];

const Products = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [category, setCategory] = useState('All');
  const [priceRange, setPriceRange] = useState([0, 1000]);

  const handlePriceChange = (event, newValue) => {
    setPriceRange(newValue);
  };

  const filteredProducts = products.filter((product) => {
    const matchesSearch = product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         product.description.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesCategory = category === 'All' || product.category === category;
    const matchesPrice = product.price >= priceRange[0] && product.price <= priceRange[1];

    return matchesSearch && matchesCategory && matchesPrice;
  });

  return (
    <Container maxWidth="lg" sx={{ mt: { xs: 12, sm: 14 }, mb: 6 }}>
      <Typography 
        variant="h2" 
        sx={{ 
          mb: 6,
          background: 'linear-gradient(45deg, #7c4dff, #1a237e)',
          WebkitBackgroundClip: 'text',
          WebkitTextFillColor: 'transparent',
          textAlign: 'center',
        }}
      >
        Explore Our Products
      </Typography>

      <Grid container spacing={4}>
        <Grid item xs={12} md={3}>
          <Card
            sx={{
              background: 'rgba(255,255,255,0.05)',
              backdropFilter: 'blur(10px)',
              border: '1px solid rgba(255,255,255,0.1)',
              position: 'sticky',
              top: 100,
            }}
          >
            <CardContent>
              <Typography variant="h6" sx={{ mb: 3, color: '#fff' }}>
                Filters
              </Typography>

              <TextField
                fullWidth
                variant="outlined"
                placeholder="Search products..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                sx={{
                  mb: 3,
                  '& .MuiOutlinedInput-root': {
                    color: '#fff',
                    '& fieldset': {
                      borderColor: 'rgba(255,255,255,0.23)',
                    },
                    '&:hover fieldset': {
                      borderColor: 'rgba(255,255,255,0.5)',
                    },
                    '&.Mui-focused fieldset': {
                      borderColor: '#7c4dff',
                    },
                  },
                }}
                InputProps={{
                  startAdornment: (
                    <InputAdornment position="start">
                      <SearchIcon sx={{ color: 'rgba(255,255,255,0.7)' }} />
                    </InputAdornment>
                  ),
                }}
              />

              <FormControl 
                fullWidth 
                sx={{ 
                  mb: 3,
                  '& .MuiOutlinedInput-root': {
                    color: '#fff',
                    '& fieldset': {
                      borderColor: 'rgba(255,255,255,0.23)',
                    },
                    '&:hover fieldset': {
                      borderColor: 'rgba(255,255,255,0.5)',
                    },
                    '&.Mui-focused fieldset': {
                      borderColor: '#7c4dff',
                    },
                  },
                  '& .MuiInputLabel-root': {
                    color: 'rgba(255,255,255,0.7)',
                    '&.Mui-focused': {
                      color: '#7c4dff',
                    },
                  },
                }}
              >
                <InputLabel>Category</InputLabel>
                <Select
                  value={category}
                  label="Category"
                  onChange={(e) => setCategory(e.target.value)}
                >
                  {categories.map((cat) => (
                    <MenuItem 
                      key={cat} 
                      value={cat}
                      sx={{
                        '&:hover': {
                          background: 'rgba(124, 77, 255, 0.1)',
                        },
                      }}
                    >
                      {cat}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>

              <Box sx={{ px: 1 }}>
                <Typography sx={{ color: '#fff', mb: 2 }}>
                  Price Range
                </Typography>
                <Slider
                  value={priceRange}
                  onChange={handlePriceChange}
                  valueLabelDisplay="auto"
                  min={0}
                  max={1000}
                  sx={{
                    color: '#7c4dff',
                    '& .MuiSlider-valueLabel': {
                      background: '#7c4dff',
                    },
                  }}
                />
                <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 1 }}>
                  <Typography sx={{ color: 'rgba(255,255,255,0.7)' }}>
                    ${priceRange[0]}
                  </Typography>
                  <Typography sx={{ color: 'rgba(255,255,255,0.7)' }}>
                    ${priceRange[1]}
                  </Typography>
                </Box>
              </Box>
            </CardContent>
          </Card>
        </Grid>

        <Grid item xs={12} md={9}>
          <Grid container spacing={3}>
            {filteredProducts.map((product) => (
              <Grid item key={product.id} xs={12} sm={6} md={4}>
                <Card
                  sx={{
                    height: '100%',
                    display: 'flex',
                    flexDirection: 'column',
                    background: 'rgba(255,255,255,0.05)',
                    backdropFilter: 'blur(10px)',
                    border: '1px solid rgba(255,255,255,0.1)',
                    transition: 'all 0.3s ease-in-out',
                    '&:hover': {
                      transform: 'translateY(-8px)',
                      boxShadow: '0 12px 20px rgba(0,0,0,0.2)',
                      border: '1px solid rgba(124, 77, 255, 0.3)',
                    },
                  }}
                >
                  <CardMedia
                    component="img"
                    height="200"
                    image={product.image}
                    alt={product.name}
                    sx={{ 
                      objectFit: 'cover',
                      borderBottom: '1px solid rgba(255,255,255,0.1)',
                    }}
                  />
                  <CardContent sx={{ flexGrow: 1, p: 3 }}>
                    <Typography 
                      gutterBottom 
                      variant="h6" 
                      component="h2"
                      sx={{
                        fontWeight: 600,
                        color: '#fff',
                      }}
                    >
                      {product.name}
                    </Typography>
                    <Typography 
                      sx={{
                        color: 'rgba(255,255,255,0.7)',
                        mb: 2,
                        fontSize: '0.875rem',
                      }}
                    >
                      {product.description}
                    </Typography>
                    <Typography 
                      variant="h6" 
                      sx={{ 
                        color: '#7c4dff',
                        fontWeight: 600,
                      }}
                    >
                      ${product.price.toFixed(2)}
                    </Typography>
                  </CardContent>
                  <CardActions sx={{ p: 2, pt: 0 }}>
                    <Button
                      fullWidth
                      variant="contained"
                      sx={{
                        background: 'rgba(124, 77, 255, 0.8)',
                        '&:hover': { 
                          background: 'rgba(124, 77, 255, 1)',
                          transform: 'translateY(-2px)',
                        },
                        transition: 'all 0.3s ease-in-out',
                      }}
                    >
                      Add to Cart
                    </Button>
                  </CardActions>
                </Card>
              </Grid>
            ))}
          </Grid>
        </Grid>
      </Grid>
    </Container>
  );
};

export default Products;
