import { createTheme } from '@mui/material/styles';

const theme = createTheme({
  palette: {
    mode: 'dark',
    primary: {
      main: '#00b4d8', // Bright blue like Solar System Scope
      light: '#48cae4',
      dark: '#023e8a',
    },
    secondary: {
      main: '#7209b7', // Deep purple for cosmic accents
      light: '#9d4edd',
      dark: '#3c096c',
    },
    background: {
      default: '#03071e', // Deep space black
      paper: 'rgba(3, 7, 30, 0.8)', // Translucent space black
    },
    cosmic: {
      nebula: '#d00000', // Red nebula
      star: '#ffba08', // Bright star
      galaxy: '#8338ec', // Galaxy purple
      meteor: '#ff006e', // Meteor pink
    },
    text: {
      primary: '#ffffff',
      secondary: 'rgba(255, 255, 255, 0.7)',
    },
  },
  typography: {
    fontFamily: '"Exo 2", "Roboto", "Helvetica", "Arial", sans-serif',
    h1: {
      fontWeight: 700,
      letterSpacing: '0.02em',
    },
    h2: {
      fontWeight: 600,
      letterSpacing: '0.02em',
    },
    h3: {
      fontWeight: 600,
      letterSpacing: '0.02em',
    },
    h4: {
      fontWeight: 600,
      letterSpacing: '0.02em',
    },
    h5: {
      fontWeight: 500,
      letterSpacing: '0.02em',
    },
    h6: {
      fontWeight: 500,
      letterSpacing: '0.02em',
    },
  },
  components: {
    MuiButton: {
      styleOverrides: {
        root: {
          textTransform: 'none',
          borderRadius: 8,
          padding: '8px 16px',
          transition: 'all 0.2s ease-in-out',
          '&:hover': {
            transform: 'translateY(-2px)',
          },
        },
        contained: {
          background: 'linear-gradient(45deg, #00b4d8 30%, #48cae4 90%)',
          boxShadow: '0 3px 10px rgba(0, 180, 216, 0.3)',
          '&:hover': {
            background: 'linear-gradient(45deg, #48cae4 30%, #00b4d8 90%)',
            boxShadow: '0 5px 15px rgba(0, 180, 216, 0.4)',
          },
        },
      },
    },
    MuiCard: {
      styleOverrides: {
        root: {
          background: 'rgba(3, 7, 30, 0.8)',
          backdropFilter: 'blur(10px)',
          borderRadius: 16,
          border: '1px solid rgba(255, 255, 255, 0.1)',
          transition: 'all 0.3s ease-in-out',
          '&:hover': {
            transform: 'translateY(-4px)',
            boxShadow: '0 8px 20px rgba(0, 180, 216, 0.2)',
          },
        },
      },
    },
    MuiChip: {
      styleOverrides: {
        root: {
          borderRadius: 8,
        },
        filled: {
          background: 'rgba(0, 180, 216, 0.2)',
          '&:hover': {
            background: 'rgba(0, 180, 216, 0.3)',
          },
        },
      },
    },
    MuiTextField: {
      styleOverrides: {
        root: {
          '& .MuiOutlinedInput-root': {
            '& fieldset': {
              borderColor: 'rgba(255, 255, 255, 0.2)',
              borderRadius: 8,
            },
            '&:hover fieldset': {
              borderColor: 'rgba(255, 255, 255, 0.3)',
            },
            '&.Mui-focused fieldset': {
              borderColor: '#00b4d8',
            },
          },
        },
      },
    },
  },
  shape: {
    borderRadius: 8,
  },
});

export default theme;
