import React from 'react';
import { Link } from 'react-router-dom';
import {
  Box,
  Container,
  Typography,
  Button,
} from '@mui/material';
import { ShoppingCart as ShoppingCartIcon } from '@mui/icons-material';

const Cart = () => {
  return (
    <Container maxWidth="lg" sx={{ py: 8 }}>
      <Box
        sx={{
          minHeight: '60vh',
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          justifyContent: 'center',
          textAlign: 'center',
          background: 'rgba(255,255,255,0.05)',
          backdropFilter: 'blur(10px)',
          borderRadius: 4,
          border: '1px solid rgba(255,255,255,0.1)',
          p: 4,
        }}
      >
        <ShoppingCartIcon
          sx={{
            fontSize: 100,
            color: 'rgba(255,255,255,0.2)',
            mb: 3,
          }}
        />
        <Typography variant="h4" sx={{ color: 'white', mb: 2 }}>
          Your Cart is Empty
        </Typography>
        <Typography
          variant="body1"
          sx={{
            color: 'rgba(255,255,255,0.7)',
            mb: 4,
            maxWidth: 600,
          }}
        >
          Explore our collection of telescopes, space merchandise, and educational materials.
          Your journey to the stars begins with a single purchase!
        </Typography>
        <Button
          component={Link}
          to="/products"
          variant="contained"
          size="large"
          sx={{
            bgcolor: '#7c4dff',
            '&:hover': {
              bgcolor: '#6c3fff',
              transform: 'translateY(-2px)',
            },
            transition: 'all 0.3s ease-in-out',
          }}
        >
          Start Shopping
        </Button>
      </Box>
    </Container>
  );
};

export default Cart;
