import React from 'react';
import {
  Box,
  Container,
  Typography,
  Button,
  Paper,
  Divider,
} from '@mui/material';
import { CheckCircle, LocalShipping } from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';

const OrderSuccess = () => {
  const navigate = useNavigate();

  return (
    <Box
      sx={{
        py: 8,
        minHeight: '100vh',
        display: 'flex',
        alignItems: 'center',
      }}
    >
      <Container maxWidth="sm">
        <Paper
          sx={{
            p: { xs: 3, md: 4 },
            textAlign: 'center',
            position: 'relative',
            overflow: 'hidden',
            '&::before': {
              content: '""',
              position: 'absolute',
              top: 0,
              left: 0,
              right: 0,
              height: '4px',
              background: 'linear-gradient(90deg, #00B6F1, #4CDFFF)',
            },
          }}
        >
          <Box
            sx={{
              width: '80px',
              height: '80px',
              mx: 'auto',
              mb: 3,
              position: 'relative',
              '&::before': {
                content: '""',
                position: 'absolute',
                top: '50%',
                left: '50%',
                transform: 'translate(-50%, -50%)',
                width: '100%',
                height: '100%',
                borderRadius: '50%',
                background: 'linear-gradient(45deg, #00B6F1, #4CDFFF)',
                opacity: 0.2,
                animation: 'pulse 2s infinite',
              },
            }}
          >
            <CheckCircle
              sx={{
                width: '100%',
                height: '100%',
                color: '#00B6F1',
                animation: 'scale 0.5s ease-out',
              }}
            />
          </Box>

          <Typography
            variant="h4"
            gutterBottom
            sx={{
              fontWeight: 700,
              background: 'linear-gradient(45deg, #00B6F1 30%, #4CDFFF 90%)',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
              animation: 'fadeIn 0.5s ease-out',
            }}
          >
            Order Confirmed!
          </Typography>

          <Typography variant="body1" color="text.secondary" paragraph>
            Thank you for shopping with Cosmos Shop. Your order has been successfully placed.
          </Typography>

          <Box
            sx={{
              my: 4,
              p: 2,
              borderRadius: 2,
              bgcolor: 'rgba(0, 182, 241, 0.1)',
              border: '1px solid rgba(0, 182, 241, 0.2)',
            }}
          >
            <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'center', mb: 1 }}>
              <LocalShipping sx={{ mr: 1, color: '#00B6F1' }} />
              <Typography variant="subtitle1" sx={{ color: '#00B6F1' }}>
                Estimated Delivery
              </Typography>
            </Box>
            <Typography variant="body2" color="text.secondary">
              Your order will be delivered within 5-7 business days
            </Typography>
          </Box>

          <Divider sx={{ my: 3 }} />

          <Box sx={{ mt: 3 }}>
            <Button
              variant="contained"
              onClick={() => navigate('/')}
              sx={{
                minWidth: 200,
                mb: 2,
              }}
            >
              Continue Shopping
            </Button>
          </Box>

          <Typography variant="body2" color="text.secondary" sx={{ mt: 2 }}>
            A confirmation email has been sent to your registered email address
          </Typography>
        </Paper>
      </Container>

      <style>
        {`
          @keyframes pulse {
            0% {
              transform: translate(-50%, -50%) scale(1);
              opacity: 0.2;
            }
            50% {
              transform: translate(-50%, -50%) scale(1.2);
              opacity: 0.1;
            }
            100% {
              transform: translate(-50%, -50%) scale(1);
              opacity: 0.2;
            }
          }

          @keyframes scale {
            from {
              transform: scale(0);
            }
            to {
              transform: scale(1);
            }
          }

          @keyframes fadeIn {
            from {
              opacity: 0;
              transform: translateY(20px);
            }
            to {
              opacity: 1;
              transform: translateY(0);
            }
          }
        `}
      </style>
    </Box>
  );
};

export default OrderSuccess;
