import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import {
  Container,
  Grid,
  Card,
  CardContent,
  CardMedia,
  Typography,
  Box,
  TextField,
  Slider,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Button,
} from '@mui/material';

const categories = [
  'All',
  'Telescopes & Accessories',
  'Space Merchandise',
  'Books & Education',
  'Home & Lifestyle',
  'Gadgets & Kits',
];

const products = [
  // Telescopes & Accessories
  {
    id: 1,
    name: 'Matrix Phoenix Refractor Telescope',
    description: 'Perfect for beginners, featuring 60/700 refractor and aluminum tripod with 1.25" eyepieces',
    price: 3999,
    category: 'Telescopes & Accessories',
    image: 'https://images.unsplash.com/photo-1579532537598-459ecdaf39cc?auto=format&fit=crop&w=1024&q=80',
  },
  {
    id: 2,
    name: 'Celestron AstroMaster Motor Drive',
    description: 'Professional telescope with motor drive and automatic tracking for deep space observation',
    price: 32999,
    category: 'Telescopes & Accessories',
    image: 'https://images.unsplash.com/photo-1614732414444-096e5f1122d5?auto=format&fit=crop&w=1024&q=80',
  },
  {
    id: 3,
    name: 'SSEA Professional Astronomical',
    description: 'Intermediate-level telescope with 70mm aperture and phone adapter for astrophotography',
    price: 7499,
    category: 'Telescopes & Accessories',
    image: 'https://images.unsplash.com/photo-1566345984367-fa2c65f33e46?auto=format&fit=crop&w=1024&q=80',
  },
  {
    id: 4,
    name: 'Astronomical Reflector Telescope',
    description: 'Advanced telescope with 114mm aperture and motor drive for precise tracking',
    price: 15999,
    category: 'Telescopes & Accessories',
    image: 'https://images.unsplash.com/photo-1543722530-d2c3201371e7?auto=format&fit=crop&w=1024&q=80',
  },
  // Space Merchandise
  {
    id: 5,
    name: 'NASA Vintage Logo T-Shirt',
    description: 'Classic NASA logo t-shirt made from premium cotton, perfect for space enthusiasts',
    price: 999,
    category: 'Space Merchandise',
    image: 'https://images.unsplash.com/photo-1517976487492-5750f3195933?auto=format&fit=crop&w=1024&q=80',
  },
  {
    id: 6,
    name: 'Astronaut Mission Patch Set',
    description: 'Collection of authentic NASA mission patches from historic space missions',
    price: 1499,
    category: 'Space Merchandise',
    image: 'https://images.unsplash.com/photo-1454789476662-53eb23ba5907?auto=format&fit=crop&w=1024&q=80',
  },
  // Books & Education
  {
    id: 7,
    name: 'Complete Astronomy Guide',
    description: 'Comprehensive guide to astronomy for all skill levels with detailed star charts',
    price: 799,
    category: 'Books & Education',
    image: 'https://images.unsplash.com/photo-1614935151651-0bea6508db6b?auto=format&fit=crop&w=1024&q=80',
  },
  {
    id: 8,
    name: 'Professional Star Charts',
    description: 'Set of detailed star charts for constellation mapping and celestial navigation',
    price: 1299,
    category: 'Books & Education',
    image: 'https://images.unsplash.com/photo-1532012197267-da84d127e765?auto=format&fit=crop&w=1024&q=80',
  },
  // Home & Lifestyle
  {
    id: 9,
    name: 'Galaxy Projector Pro',
    description: 'Transform your room into a cosmic wonderland with nebula effects and music sync',
    price: 2499,
    category: 'Home & Lifestyle',
    image: 'https://images.unsplash.com/photo-1534293230397-c067fc201ab8?auto=format&fit=crop&w=1024&q=80',
  },
  {
    id: 10,
    name: 'Cosmic Bedding Set',
    description: 'Premium space-themed bedding set with galaxy print duvet cover and pillowcases',
    price: 1999,
    category: 'Home & Lifestyle',
    image: 'https://images.unsplash.com/photo-1506318137071-a8e063b4bec0?auto=format&fit=crop&w=1024&q=80',
  },
  // Gadgets & Kits
  {
    id: 11,
    name: 'DIY Mars Rover Kit',
    description: 'Build your own Mars rover with this educational kit, includes programming module',
    price: 3499,
    category: 'Gadgets & Kits',
    image: 'https://images.unsplash.com/photo-1446776811953-b23d57bd21aa?auto=format&fit=crop&w=1024&q=80',
  },
  {
    id: 12,
    name: 'Smart Telescope Kit',
    description: 'Advanced telescope kit with smartphone control and automatic star tracking',
    price: 24999,
    category: 'Gadgets & Kits',
    image: 'https://images.unsplash.com/photo-1543722530-d2c3201371e7?auto=format&fit=crop&w=1024&q=80',
  },
];

const Products = () => {
  const location = useLocation();
  const [searchTerm, setSearchTerm] = useState('');
  const [category, setCategory] = useState('All');
  const [priceRange, setPriceRange] = useState([0, 33000]);

  useEffect(() => {
    if (location.state?.category) {
      setCategory(location.state.category);
    }
  }, [location.state]);

  const handlePriceChange = (event, newValue) => {
    setPriceRange(newValue);
  };

  const handleCategoryChange = (event) => {
    setCategory(event.target.value);
  };

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
  };

  const filteredProducts = products.filter((product) => {
    const matchesSearch = product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      product.description.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesCategory = category === 'All' || product.category === category;
    const matchesPrice = product.price >= priceRange[0] && product.price <= priceRange[1];
    return matchesSearch && matchesCategory && matchesPrice;
  });

  const formatPrice = (value) => {
    return `â‚¹${value.toLocaleString()}`;
  };

  return (
    <Container sx={{ py: 8 }}>
      {/* Filters Section */}
      <Box sx={{ mb: 6 }}>
        <Grid container spacing={4}>
          <Grid item xs={12} sm={4}>
            <TextField
              fullWidth
              variant="outlined"
              placeholder="Search products..."
              value={searchTerm}
              onChange={handleSearch}
              sx={{
                mb: 3,
                '& .MuiOutlinedInput-root': {
                  color: 'white',
                  '& fieldset': {
                    borderColor: 'rgba(255,255,255,0.23)',
                  },
                  '&:hover fieldset': {
                    borderColor: 'rgba(124,77,255,0.5)',
                  },
                  '&.Mui-focused fieldset': {
                    borderColor: '#7c4dff',
                  },
                },
                '& .MuiInputLabel-root': {
                  color: 'rgba(255,255,255,0.7)',
                },
              }}
            />
          </Grid>
          <Grid item xs={12} sm={4}>
            <FormControl fullWidth>
              <InputLabel 
                id="category-select-label"
                sx={{ 
                  color: 'rgba(255,255,255,0.7)',
                  '&.Mui-focused': {
                    color: '#7c4dff',
                  },
                }}
              >
                Category
              </InputLabel>
              <Select
                labelId="category-select-label"
                value={category}
                label="Category"
                onChange={handleCategoryChange}
                sx={{
                  color: 'white',
                  '.MuiOutlinedInput-notchedOutline': {
                    borderColor: 'rgba(255,255,255,0.23)',
                  },
                  '&:hover .MuiOutlinedInput-notchedOutline': {
                    borderColor: 'rgba(124,77,255,0.5)',
                  },
                  '&.Mui-focused .MuiOutlinedInput-notchedOutline': {
                    borderColor: '#7c4dff',
                  },
                  '.MuiSvgIcon-root': {
                    color: 'white',
                  },
                }}
              >
                {categories.map((cat) => (
                  <MenuItem 
                    key={cat} 
                    value={cat}
                    sx={{
                      '&:hover': {
                        bgcolor: 'rgba(124,77,255,0.1)',
                      },
                      '&.Mui-selected': {
                        bgcolor: 'rgba(124,77,255,0.2)',
                        '&:hover': {
                          bgcolor: 'rgba(124,77,255,0.3)',
                        },
                      },
                    }}
                  >
                    {cat}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>
          <Grid item xs={12} sm={4}>
            <Typography sx={{ color: 'rgba(255,255,255,0.7)', mb: 1 }}>
              Price Range: {formatPrice(priceRange[0])} - {formatPrice(priceRange[1])}
            </Typography>
            <Slider
              value={priceRange}
              onChange={handlePriceChange}
              min={0}
              max={33000}
              step={100}
              sx={{
                color: '#7c4dff',
                '& .MuiSlider-thumb': {
                  '&:hover, &.Mui-focusVisible': {
                    boxShadow: '0 0 0 8px rgba(124,77,255,0.16)',
                  },
                },
                '& .MuiSlider-rail': {
                  opacity: 0.32,
                },
              }}
            />
          </Grid>
        </Grid>
      </Box>

      {/* Products Grid */}
      <Grid container spacing={4}>
        {filteredProducts.map((product) => (
          <Grid item key={product.id} xs={12} sm={6} md={4}>
            <Card
              sx={{
                height: '100%',
                display: 'flex',
                flexDirection: 'column',
                background: 'rgba(255,255,255,0.05)',
                backdropFilter: 'blur(10px)',
                border: '1px solid rgba(255,255,255,0.1)',
                transition: 'all 0.3s ease-in-out',
                '&:hover': {
                  transform: 'translateY(-8px)',
                  boxShadow: '0 12px 20px rgba(0,0,0,0.2)',
                  border: '1px solid rgba(124,77,255,0.3)',
                  '& .MuiCardMedia-root': {
                    transform: 'scale(1.1)',
                  },
                },
              }}
            >
              <Box sx={{ position: 'relative', overflow: 'hidden', pt: '60%' }}>
                <CardMedia
                  component="img"
                  image={product.image}
                  alt={product.name}
                  sx={{
                    position: 'absolute',
                    top: 0,
                    left: 0,
                    width: '100%',
                    height: '100%',
                    transition: 'transform 0.3s ease-in-out',
                  }}
                />
                <Box
                  sx={{
                    position: 'absolute',
                    top: 0,
                    left: 0,
                    right: 0,
                    bottom: 0,
                    background: 'linear-gradient(to bottom, rgba(0,0,0,0.4), rgba(0,0,0,0.1))',
                  }}
                />
              </Box>
              <CardContent sx={{ flexGrow: 1 }}>
                <Typography variant="h5" component="h2" sx={{ color: 'white', mb: 1 }}>
                  {product.name}
                </Typography>
                <Typography sx={{ color: 'rgba(255,255,255,0.7)', mb: 2 }}>
                  {product.description}
                </Typography>
                <Typography variant="h6" sx={{ color: '#7c4dff' }}>
                  {formatPrice(product.price)}
                </Typography>
              </CardContent>
              <Box sx={{ p: 2, pt: 0 }}>
                <Button
                  fullWidth
                  variant="contained"
                  sx={{
                    bgcolor: '#7c4dff',
                    '&:hover': {
                      bgcolor: '#6c3fff',
                      transform: 'translateY(-2px)',
                    },
                    transition: 'all 0.3s ease-in-out',
                  }}
                >
                  Add to Cart
                </Button>
              </Box>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
};

export default Products;
