import React from 'react';
import { useLocation, useParams } from 'react-router-dom';
import {
  Box,
  Container,
  Typography,
  Grid,
  Card,
  CardContent,
  CardMedia,
  Button,
  IconButton,
  Chip,
} from '@mui/material';
import { FavoriteBorder, Favorite, ShoppingCart } from '@mui/icons-material';
import { useProduct } from '../context/ProductContext';
import { useTheme } from '@mui/material/styles';

const categoryProducts = {
  telescopes: [
    {
      id: 1,
      name: 'Matrix Phoenix Refractor Telescope',
      price: 3999,
      description: '60/700 refractor, aluminum tripod, 1.25" eyepieces - Perfect for beginners',
      image: 'https://images.unsplash.com/photo-1579532537598-459ecdaf39cc?auto=format&fit=crop&w=1024&q=80',
      target: 'Beginners',
      features: ['60/700 refractor', 'Aluminum tripod', '1.25" eyepieces'],
    },
    {
      id: 2,
      name: 'Celestron AstroMaster Motor Drive',
      price: 32999,
      description: 'Professional telescope with motor drive and automatic tracking system',
      image: 'https://images.unsplash.com/photo-1566345984367-fa2ba5e69182?auto=format&fit=crop&w=1024&q=80',
      target: 'Professional',
      features: ['Motor drive', 'Automatic tracking', 'Professional grade optics'],
    },
    {
      id: 3,
      name: 'SSEA Professional Astronomical',
      price: 7499,
      description: '70mm aperture telescope with phone adapter for astrophotography',
      image: 'https://images.unsplash.com/photo-1543722530-d2c3201371e7?auto=format&fit=crop&w=1024&q=80',
      target: 'Intermediate',
      features: ['70mm aperture', 'Phone adapter', 'Portable design'],
    },
    {
      id: 4,
      name: 'Astronomical Reflector Telescope',
      price: 15999,
      description: '114mm aperture reflector telescope with motor drive for advanced users',
      image: 'https://images.unsplash.com/photo-1567173277-3c2051b3ec58?auto=format&fit=crop&w=1024&q=80',
      target: 'Advanced',
      features: ['114mm aperture', 'Motor drive', 'Advanced optics'],
    }
  ],
  merchandise: [
    {
      id: 5,
      name: 'NASA Apollo Mission Poster Set',
      price: 1499,
      description: 'Collection of vintage-style NASA mission posters',
      image: 'https://images.unsplash.com/photo-1517976487492-5750f3195933?auto=format&fit=crop&w=1024&q=80',
      target: 'Space enthusiasts',
    },
    {
      id: 6,
      name: 'Astronaut Hoodie',
      price: 1999,
      description: 'Premium quality space-themed hoodie with NASA logo',
      image: 'https://images.unsplash.com/photo-1578269174936-2709b6aeb913?auto=format&fit=crop&w=1024&q=80',
      target: 'Fashion enthusiasts',
    }
  ],
  books: [
    {
      id: 7,
      name: 'Astronomy Guide 2024',
      price: 799,
      description: 'Comprehensive guide to astronomy for beginners',
      image: 'https://images.unsplash.com/photo-1614935151651-0bea6508db6b?auto=format&fit=crop&w=1024&q=80',
      target: 'Students',
    },
    {
      id: 8,
      name: 'Star Atlas Premium',
      price: 1499,
      description: 'Detailed star charts and celestial maps',
      image: 'https://images.unsplash.com/photo-1535905557558-afc4877a26fc?auto=format&fit=crop&w=1024&q=80',
      target: 'Astronomers',
    }
  ]
};

const CategoryPage = () => {
  const theme = useTheme();
  const { categoryPath } = useParams();
  const { addToCart, addToWishlist, removeFromWishlist, isInWishlist } = useProduct();

  const products = categoryProducts[categoryPath.replace('/', '')] || [];
  const categoryName = {
    telescopes: 'Telescopes & Accessories',
    merchandise: 'Space Merchandise',
    books: 'Books & Education',
  }[categoryPath.replace('/', '')] || 'Products';

  const handleAddToWishlist = (product) => {
    if (isInWishlist(product.id)) {
      removeFromWishlist(product.id);
    } else {
      addToWishlist(product);
    }
  };

  return (
    <Box sx={{ py: 6, minHeight: '100vh' }}>
      <Container>
        <Typography
          variant="h3"
          component="h1"
          align="center"
          gutterBottom
          sx={{
            fontWeight: 700,
            background: 'linear-gradient(45deg, #2196F3 30%, #21CBF3 90%)',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            mb: 6,
          }}
        >
          {categoryName}
        </Typography>

        <Grid container spacing={4}>
          {products.map((product) => (
            <Grid item xs={12} sm={6} md={4} key={product.id}>
              <Card
                sx={{
                  height: '100%',
                  display: 'flex',
                  flexDirection: 'column',
                  position: 'relative',
                  transition: '0.3s',
                  '&:hover': {
                    transform: 'translateY(-8px)',
                    boxShadow: '0 12px 20px rgba(0,0,0,0.2)',
                  },
                }}
              >
                <CardMedia
                  component="img"
                  height="250"
                  image={product.image}
                  alt={product.name}
                  sx={{ objectFit: 'cover' }}
                />
                <IconButton
                  onClick={() => handleAddToWishlist(product)}
                  sx={{
                    position: 'absolute',
                    top: 8,
                    right: 8,
                    bgcolor: 'background.paper',
                    '&:hover': {
                      bgcolor: 'background.paper',
                    },
                  }}
                >
                  {isInWishlist(product.id) ? (
                    <Favorite sx={{ color: theme.palette.cosmic.nebula }} />
                  ) : (
                    <FavoriteBorder />
                  )}
                </IconButton>
                {product.target && (
                  <Chip
                    label={product.target}
                    size="small"
                    sx={{
                      position: 'absolute',
                      top: 8,
                      left: 8,
                      bgcolor: 'rgba(0, 0, 0, 0.6)',
                      color: 'white',
                    }}
                  />
                )}
                <CardContent sx={{ flexGrow: 1, p: 3 }}>
                  <Typography
                    variant="h6"
                    component="h2"
                    gutterBottom
                    sx={{
                      fontWeight: 600,
                      minHeight: 64,
                      display: '-webkit-box',
                      WebkitLineClamp: 2,
                      WebkitBoxOrient: 'vertical',
                      overflow: 'hidden',
                    }}
                  >
                    {product.name}
                  </Typography>
                  <Typography
                    variant="body2"
                    color="text.secondary"
                    paragraph
                    sx={{
                      minHeight: 60,
                      display: '-webkit-box',
                      WebkitLineClamp: 2,
                      WebkitBoxOrient: 'vertical',
                      overflow: 'hidden',
                    }}
                  >
                    {product.description}
                  </Typography>
                  {product.features && (
                    <Box sx={{ mb: 2 }}>
                      {product.features.map((feature, index) => (
                        <Chip
                          key={index}
                          label={feature}
                          size="small"
                          sx={{
                            mr: 1,
                            mb: 1,
                            bgcolor: 'rgba(33, 150, 243, 0.1)',
                          }}
                        />
                      ))}
                    </Box>
                  )}
                  <Typography
                    variant="h6"
                    sx={{ color: 'primary.main', mb: 2 }}
                  >
                    â‚¹{product.price.toLocaleString()}
                  </Typography>
                  <Button
                    fullWidth
                    variant="contained"
                    startIcon={<ShoppingCart />}
                    onClick={() => addToCart(product)}
                    sx={{
                      background: `linear-gradient(45deg, ${theme.palette.primary.main} 30%, ${theme.palette.primary.light} 90%)`,
                      color: 'background.paper',
                      '&:hover': {
                        background: `linear-gradient(45deg, ${theme.palette.primary.light} 30%, ${theme.palette.primary.main} 90%)`,
                      },
                    }}
                  >
                    Add to Cart
                  </Button>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Container>
    </Box>
  );
};

export default CategoryPage;
